void mainImage ( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord.xy / iResolution.xy-0.5) * 2.;
    uv.x *= iResolution.x / iResolution.y;

    float time = iTime * 1.0;
    float timeSin = sin(time);

    float timeSinHalfWhole = (((timeSin+1.)/2.)/2.)+0.5;

    float value = distance(vec2(0,0), uv);

    float glow1 = smoothstep(value, 0.5,value-.05 * timeSinHalfWhole);

    float glow2 = smoothstep(value, 0.5,value-.025 * timeSinHalfWhole);

    vec3 glowColor = vec3(1.,0.,0.);

    vec3 redGlow1 = (vec3(glow1,glow1,glow1) * glowColor);

    vec3 redGlow1AddGlow2 = redGlow1 + vec3(glow2,glow2,glow2);


    vec3 vec3fromValue = vec3(timeSinHalfWhole,timeSinHalfWhole,timeSinHalfWhole);

    fragColor = vec4(redGlow1AddGlow2 , 1);
}
