float fraq( float v ){
 return v-floor(v);
}

// dhr_random, version "brandis2"
float myfn( float v ){
 return fraq(v*fraq(v*0.71391)*fraq(v*0.90347));
}

void mainImage( out vec4 fragColor, in vec2 c )
{   
    float w = iResolution.x;
    float t = iTime*5.;
    float t2= t+0.5;
    float b = c.x*0.011 + c.y*w*0.011;
    float v1 = myfn( b + floor(t) );
    float v2 = myfn( b + floor(t)+1.0 );
    float v3 = myfn( 2852.34234 + b + floor(t2)     );
    float v4 = myfn( 2852.34234 + b + floor(t2)+1.0 );
    float v = 0.5*((v1+(v2-v1)*fraq(t))+(v3+(v4-v3)*fraq(t2))); 

    vec3 col = vec3( v, v, v );

    // Output to screen
    fragColor = vec4(col,1.0);
}
