// The SDFs used here are iq's https://iquilezles.org/

float dot2(vec2 p) {
    return dot(p, p);
}

float sdCircle(vec2 p, float r) {
    return length(p) - r;
}

float sdHeart(vec2 p, float scl, float yp) {
    p *= scl;
    p.y += yp;
    p.x = abs(p.x);

    if(p.y + p.x > 1.0) return (sqrt(dot2(p - vec2(0.25, 0.75))) - sqrt(2.0) / 4.0) / scl;
    return (sqrt(min(dot2(p - vec2(0.00, 1.00)), dot2(p - 0.5 * max(p.x + p.y, 0.0)))) * sign(p.x - p.y)) / scl;
}

float map(vec2 p) {
    float d0 = sdCircle(p, 0.3);
    float d1 = sdHeart(p, 1.6, 0.55);
    return abs(mix(d1, d0, cos(iTime) * 0.5 + 0.5)) - 0.03;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 col = vec3(1.0, 1.0, 0.0);
    float mp = map(uv);
    float tf = smoothstep(0.005, 0.0, mp);
    col = mix(vec3(1.0, 1.0, 0.0), vec3(1.0, 0.0, 1.0), tf);
    fragColor = vec4(col, 1.0);
}
