void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv -= 0.5;
    uv *= 2.0;
    uv.x*= iResolution.x/iResolution.y;

    vec3 col = vec3(0.0,0.0,0.0);

    uv *= 2.0;
    uv = fract(uv);
    uv -= 0.5;

    for(float i = 0.0; i < 1.0; i+= 1.0)
    {

        float d = length(uv);
        d += sin(d + iTime);
        d = pow(d, 2.0);

        d = fract(d);

        col = vec3(d);
        col.x = 1.0;
        col.y += 0.5;
        col.z -= 0.5;
    }



    fragColor = vec4(col, 1.0);
}
