vec4 cheapAtmosphereScattering(in vec2 uv) {

    float r = length(uv);

    vec3 col1 = vec3(159,233,245)/255.;    
    vec3 col2 = vec3(119,190,251)/255.;

    float R0 = 0.25;
    float R1 = 0.38;
    float R2 = 0.4;
    float R3 = 0.5;    

    if(r < R1) {
        float transition = pow(max(0., (r-R0) / (R1-R0)), 2.);        
        return vec4(col1*transition, 1.);
    }

    if(r < R2) {
        float transition = pow((r-R1)/(R2-R1), 2.);
        return vec4(mix(col1, col2, transition), 1.);
    }

    float transition = pow(max(0., 1. - (r-R2)/(R3-R2)), 2.);
    return vec4(col2 * transition, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - iResolution.xy*0.5)/iResolution.y;
    fragColor = cheapAtmosphereScattering(uv);
}
