void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = 2.0 * (fragCoord.xy - 0.5 * iResolution.xy)/iResolution.y;
    vec2 m = 2.0 * (iMouse.xy - 0.5 * iResolution.xy)/iResolution.y;
    float xMax = iResolution.x/iResolution.y;
    float scale = 4.0 - 2.5 * cos(iTime/2.0);

    // Mouse position determines each color channel
    float r = cos(scale * length(uv - m));
    float g = sin(scale * (uv.x + m.x));
    float b = cos(scale * (uv.y + m.y));

    float vign;
    if(xMax - abs(uv.x) < 1.0 - abs(uv.y)) {
        vign = smoothstep(xMax - 0.5, xMax, abs(uv.x));
    }
    else {
        vign = smoothstep(0.5, 1.0, abs(uv.y));
    }
    vec3 col = mix(0.6 + 0.4 * vec3(r, g, b), vec3(0.0), 0.4 * vign);
    // Output to screen
    fragColor = vec4(col,1.0);
}
