void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*.5)/(.5*iResolution.y);
    vec2 mouseUV = (iMouse.xy-iResolution.xy*.5)/(.5*iResolution.y);
    uv -= mouseUV; // rotate around mouseUV
    float t = iTime;
    mat2 rot = mat2(cos(t), -sin(t), sin(t), cos(t));
    uv *= rot;
    float f = 2.5; // length of the gradient in screen heights
    // move & scale so origin is at .5 and +- y go from 0 to 1
    uv += f * vec2(0., 1.);
    uv /= f * 2.;

    vec3 col1 = vec3(1., 0., 0.);
    vec3 col2 = vec3(0., 1., 0.);
    vec3 col = mix(col1, col2, uv.y);

    // Output to screen
    fragColor = vec4(col,1.0);
}
