void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_pixel_cord_norm = fragCoord.xy / iResolution.xy; 
    vec2 o_mouse_cord_norm = iMouse.xy / iResolution.xy; 
    if(iMouse.z == 0.0){
        o_mouse_cord_norm = vec2(0.5,0.5);
    }
    float n_dist_pix_mouse = length(
        o_pixel_cord_norm - o_mouse_cord_norm
    );
    float n_stretch_factor = 1.5;
    vec2 n_deviation_mouse = (o_mouse_cord_norm-vec2(0.5))*-vec2(n_dist_pix_mouse)*n_stretch_factor;

    float n_dist_pix_mouse_with_deviation = length(
        o_pixel_cord_norm - (o_mouse_cord_norm+(n_deviation_mouse))
    );
    float n_dist_pix_mouse_modified = pow(n_dist_pix_mouse_with_deviation, 1.0/2.0);
    float n_dist_pix_mouse_modified_mod_norm = 
        mod(
            n_dist_pix_mouse_modified
            ,0.2
            )/0.2;

    float n_tau = 6.2831;
    float n = mod(n_dist_pix_mouse_modified_mod_norm+0.5, 1.0);

    float l = 2.0;
    float a = pow(n-(1.0/2.0), l) * pow(2.0,l);


    fragColor = vec4(a);
}
