renderpass.code:
mat2 rot(in float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

float sd_box(vec2 p, vec2 h) {
    p = abs(p) - h;
    return length(max(p, 0.)) + min(0., max(p.x, p.y));
}

float map(vec3 p) {
    float time = iTime;
    float a = atan(p.y, p.x) ;
    vec3 q = p;
    /* q.xz = polarSmoothFold(q.xz, 4, 0.1); */
    float f = length(q.xy) - 0.5;
    vec2 tube = vec2(f, p.z);
    tube.x *= cos(a) * tan(a);
    tube *= rot(a + time);
    float d = length(tube) - 0.15;
    /* d = max(abs(tube.x), abs(tube.y)) - 0.1; */
    d = sd_box(tube, vec2(0.1));
    return d;
}

vec3 get_norm(vec3 p) {
    mat3 k = mat3(p,p,p) - mat3(0.0001);
    return normalize(map(p) - vec3(map(k[0]), map(k[1]), map(k[2])));
}

mat3 get_cam(vec3 eye, vec3 target) {
    vec3 zaxis = normalize(target - eye);
    vec3 xaxis = normalize(cross(zaxis, vec3(0, 1., 0.)));
    vec3 yaxis = cross(xaxis, zaxis);
    return mat3(xaxis, yaxis, zaxis);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.x;

    vec3 col = vec3(uv, 1.);

    vec3 ro = vec3(0., 0., -3.);
    /* ro.xz *= rot(pc.time); */
    mat3 cam = get_cam(ro, vec3(0.));
    vec3 rd = cam * normalize(vec3(uv, 1.));

    float t = 0.;
    for (int i = 0; i < 100; i++) {
        vec3 pos = ro + rd * t;
        float d = map(pos);
        t += d;
        if (t > 10.) break;
    }

    if (t < 10.) {
        vec3 pos = ro + rd * t;
        vec3 nor = get_norm(pos);

        col = nor * 0.5 + 0.5;
    }

    fragColor = vec4(col,1.0);
}
