vec3 draw_sky(vec2 uv ){
    vec3 c0 = vec3(0.996,0.868,0.297) ; 
    vec3 c1  = vec3(0.996,0.711,0.515) ; 
    vec3 c2 = vec3(0.151,0.694,0.996) ; 
    float h1 = 0.05; 
    if(uv.y <  h1 ){
        if(uv.y <  0.5*h1 ){
        float gradient = (uv.y - h1 ) / (h1) ; 
        return mix(c1 , c0  , gradient)  ;  
        }
        else
        {
        float gradient = (uv.y - h1 ) / (h1) ; 
        return mix(c1 , c0  , gradient)  ;     
        }
    }else{
        float gradient = (uv.y - h1 ) / (h1) ; 
        return mix(c1 , c2  , gradient)  ;       
    }
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = draw_sky(uv) ; 

    // Output to screen
    fragColor = vec4(col,1.0);
}
