vec2 hash22(vec2 p)
{
    vec3 a = fract(p.xyx*vec3(154.34, 124.54, 891.32));
    a += dot(a, a+23.53);
    return fract(vec2(a.x*a.y, a.x*a.z));
}

float voronoi(vec2 uv)
{
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);
    float minDist = 100.;

    for(float y=-1.; y<=1.; y++)
    {
        for(float x=-1.; x<=1.; x++)
        {
            vec2 offs = vec2(x,y);
            vec2 n = hash22(id+offs);
            vec2 p = offs+sin(n*iTime*3.)*.5;

            float d = length(gv-p);
            minDist = min(minDist, d);
        }
    }

    return minDist;
}

float hash11(float co)
{
    return fract(sin(dot(co*12.9898, co*78.233)) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);
    uv*=3.;
    float w = 15./iResolution.y;

//    float sz = 1.+abs(tan(iTime));

    for(float i=0.; i<10.; i++)
    {
        vec2 p = vec2(i-4.5, mod(iTime*3.+hash11(i)*50., 10.)-4.);
        float d = length(uv-p);
        d = smoothstep(w, -w, d-voronoi(uv*5.)*1.5);
        d *= .1/voronoi(uv*5.)*2.;
        col += vec3(.5, .8, .9)*d;
    }


    fragColor = vec4(col,1.0);
}
