precision highp float;
float noise1d(float n){
    return fract(cos(n*89.42)*343.42);
}

float remap(float x, float min1,float min2, float max1, float max2){
    float remapped = min2 + (x - min1) * (max2 - min2) / (max1 - min1);
    return remapped;
}
float timeSeed = 5000.;
//1087.93
//float 
// cosine based palette, 4 vec3 params
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    float time_multiplier = 0.05;
    return a + b*cos( 6.28318*(c*(t* noise1d(time_multiplier)/2. ) + d) );
}



void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = ((fragCoord / iResolution.xy)*2.0-1.0);
    uv.x *= iResolution.x/iResolution.y;

    //TODO: Show one fract uv*1, then wait a bit and show 2, then 4, then 8, and maybe do something cool
    // to hang out in 8 for awhile then pong around from there with a bias toward decreasing consistently
    // rather than rapidly jumping to giant numbers

    uv = (fract(0.001/(uv)/1000. ) -0.5);
    //            *step(sin(iTime),mod(iTime,4.))
    float d = length(uv);

    float circle = 0.;
    circle = sin(d*9.0 *(iTime+timeSeed))/9.0;

    circle = abs(circle);
    circle = smoothstep(0.0,0.05,circle);


    //vec3 color = vec3(0.5,circle/3.,circle);
    /*
    vec3 color = palette(iTime,vec3(0.660, 0.560, 0.680),
        vec3(0.718, 0.438, 0.720),
        vec3(0.520, 0.800, 0.520),
        vec3(-0.430, -0.397, -0.083)
        );
    */
    vec3 color = palette(iTime, vec3(0.500, 0.500, 0.500), vec3(0.500, 0.500, 0.500), vec3(1.000, 1.000, 1.000), vec3(0.000, 0.333, 0.667));
    //vec3 color = palette(iTime, vec3(0.469, 0.823, 0.326), vec3(0.550, 0.928, 0.849), vec3(1.535, 0.850, 0.026), vec3(3.549, 2.004, 3.243));
    //vec3 color = palette(iTime, vec3(0.469, 0.823, 0.948), vec3(-0.962, 0.928, 0.849), vec3(-1.232, -3.142, -0.072), vec3(3.549, 2.004, 3.243));
    color *= 0.4/circle;
    fragColor =  vec4(color, 1.0);

}
