void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 mouse = iMouse.xy / iResolution.xy;
    vec2 pos = fragCoord.xy / iResolution.xy;
    float time = iTime * 0.5;

    // Generate pattern
    float r = sin(time + pos.x * 10.0) * cos(time + pos.y * 10.0);
    float g = sin(time + pos.x * 20.0) * sin(time + pos.y * 20.0);
    float b = cos(time + pos.x * 30.0) * sin(time + pos.y * 30.0);

    // Generate color based on pattern and mouse position
    float dist = distance(mouse, pos);
    float alpha = smoothstep(0.1, 0.5, dist);
    vec3 color = mix(vec3(r, g, b), vec3(1.0, 1.0, 1.0), alpha);

    // Output final color
    fragColor = vec4(color, 1.0);
}
