const float numBands = 10.;
const float thickness = 0.003;
const float animationSpeed = 0.3;
// Colors
const vec4 redColor = vec4(0.95, 0.45, 0.21, 1.);
const vec4 greenColor = vec4(0.35, 0.73, 0.3, 1.);
const vec4 backgroundColor = vec4(.3, .1, .2, 1.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Variables
    float numInterstices = numBands - 1.;
    float intersticesSpace = numInterstices * 2. * thickness;
    float barSpace = 1. - intersticesSpace;
    float bandWidth = barSpace / numBands;
    float finalResult = 0.;

    // Animate life percent over time
    float lifePercent = 10.;
    lifePercent = abs(sin(iTime * animationSpeed)) * 100.;

    // Generate gradient
    vec2 uv = fragCoord / iResolution.xy;
    float gradient = uv.x; 

    // Loop
    for(float i=1.; i < numInterstices + 1.; i++){
        float temp = 0.;

        if(i == 1.)
        {
            temp = 1.;
        }
        else
        {
            temp = (i - 1.) * 2. + 1.;
        }

        float desiredPoint = (temp * thickness) + (i * bandWidth);

        if(gradient>=desiredPoint-thickness && gradient<=desiredPoint+thickness)
        {
            finalResult+=1.;
        }
        else
        {
            finalResult+=0.;
        }
    }

    // One minus
    finalResult = 1. - finalResult;

    // Blend life color
    float lifeBar = step(1., gradient / (lifePercent / 100.));
    lifeBar = 1. - lifeBar;

    vec4 lifeBarColor = mix(redColor, greenColor, lifePercent / 100.);
    lifeBarColor = mix(backgroundColor, lifeBarColor, lifeBar);

    // Draw color
    fragColor = lifeBarColor * vec4(finalResult, finalResult, finalResult, 1.);
}
