#define R(a) mix( q*dot(p,q), p, cos(a) ) + sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.2,e; vec3 p,q=vec3(-.577);
o=vec4(0.0);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    p.z-=1.;
    p=R(iTime*.3);

    for(j=0.;j++<8.;)
        p=abs(abs(p=R(1.1))-.03)-.02,
        d=max(max(p.x,p.y),p.z),
        d-=dot(p,p)/2e4;
     e+=d;
}
o+=vec4(tan(-p/2.),1)*i*j;
}
