precision mediump float;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 u_color0 = vec4(0.125,0.643,0.843,1.0);
    vec4 u_color1 = vec4(0.247,0.545,0.918,1.0);
    vec4 u_color2 = vec4(0.506,0.282,0.925,1.0);
    vec4 u_color3 = vec4(0.706,0.337,0.847,1.0);
    vec4 u_color4 = vec4(0.271,0.463,0.914,1.0);
    vec4 u_color5 = vec4(0.353,0.694,0.278,1.0);
    vec4 u_color6 = vec4(0.027,0.729,0.388,1.0);
    vec4 u_color7 = vec4(0.027,0.663,0.675,1.0);

    float currentAngle = iTime * 0.5;
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 center = vec2(0.5, 0.5);
    float dist = distance(uv, center)-iTime*1.5;
    float radius = iTime / 5000.0;
    float alpha = smoothstep(radius, radius + 0.0001, dist);

    vec2 transformed = (uv - center) * mat2(cos(currentAngle), sin(currentAngle), -sin(currentAngle), cos(currentAngle)) + center;
    //circle
    vec4 tltr = mix(u_color4, u_color6, smoothstep(0.0, 1.0, transformed.x));
    vec4 blbr = mix(u_color5, u_color7, smoothstep(0.0, 1.0, transformed.x));
    vec4 color1 = mix(tltr, blbr, smoothstep(0.0, 1.0, transformed.y));
    //bg
    tltr = mix(u_color0, u_color1, smoothstep(0.0, 1.0, transformed.x));
    blbr = mix(u_color2, u_color3, smoothstep(0.0, 1.0, transformed.x));
    vec4 color2 = mix(tltr, blbr, smoothstep(0.0, 1.0, transformed.y));
    fragColor = mix(color1, color2, alpha);
}
