void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	// Normalized pixel coordinates (from 0 to 1)
	vec2 uv = fragCoord / iResolution.xy;
	vec2 texSize = iResolution.xy;
    // 512 is the iChannel1 image's size
	texSize = vec2(iResolution.x / 1024.0, iResolution.y / 1024.0);
	vec2 position = vec2(fract(iTime) * 2.0 - 1.0, 0.1);
	float scale = 0.7;
    float transparency = 0.9;
	vec2 texCoord = vec2((uv.x - position.x) * texSize.x / texSize.y, uv.y - position.y);
	vec4 color0 = texture(iChannel0, uv);
	vec4 color1 = texture(iChannel1, texCoord / scale);
	if (uv.x > position.x && uv.x * texSize.x / texSize.y < scale + position.x * texSize.x / texSize.y && uv.y >
		position.y && uv.y < scale * 1.0 + position.y)
	{
		fragColor = mix(color0, color1, transparency);
	}
	else
	{
		fragColor = color0;
	}
}
