vec4 swooshTexture(vec2 uv){
  float d=float(uv.x<fract(uv.y*2.));
  return vec4(d*(1.-uv.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.y;

    if(uv.x>1.0)return;


    float d = length(uv);
    if(d>1. || d<0.8){fragColor = vec4(0);return;}

    vec2 nuv;
    nuv.x=atan(uv.y,uv.x)/radians(90.);
    nuv.y=(d-0.8)/(1.-0.8);


    fragColor = vec4(nuv,0,1.0);

    fragColor = swooshTexture(nuv);
}
