renderpass.code:
float sdCircle( vec2 p, float r ){
    return length(p) - r;
}

float sdEquilateralTriangle( in vec2 p, in float r )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );
    return -length(p)*sign(p.y);
}

float dot2( in vec2 v ) { return dot(v,v); }


float sdStairs( in vec2 p, in vec2 wh, in float n )
{
    vec2 ba = wh*n;
    float d = min(dot2(p-vec2(clamp(p.x,0.0,ba.x),0.0)), 
                  dot2(p-vec2(ba.x,clamp(p.y,0.0,ba.y))) );
    float s = sign(max(-p.y,p.x-ba.x) );

    float dia = length(wh);
    p = mat2(wh.x,-wh.y, wh.y,wh.x)*p/dia;
    float id = clamp(round(p.x/dia),0.0,n-1.0);
    p.x = p.x - id*dia;
    p = mat2(wh.x, wh.y,-wh.y,wh.x)*p/dia;

    float hh = wh.y/2.0;
    p.y -= hh;
    if( p.y>hh*sign(p.x) ) s=1.0;
    p = (id<0.5 || p.x>0.0) ? p : -p;
    d = min( d, dot2(p-vec2(0.0,clamp(p.y,-hh,hh))) );
    d = min( d, dot2(p-vec2(clamp(p.x,0.0,wh.x),hh)) );

    return sqrt(d)*s;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 r =2.0*vec2(fragCoord.xy -0.5*iResolution.xy)/iResolution.y;

    vec3 col=vec3(0.);

    if(uv.y<0.2)//ground
        col=0.5*vec3(150./255., 75./255., 0.);

    col = mix(col, vec3(0.9,0.5,0.), smoothstep(0.,0.03, -sdCircle(r,0.9)));

    vec3 eyes = vec3(1.,1.,0.)*abs(1.0*sin(iTime));
    vec2 rMirrorX=vec2(abs(r.x),r.y);
    float EYE=smoothstep(0.,0.03, -sdEquilateralTriangle(rMirrorX-vec2(0.4,0.25),0.2));
    float EYE2=smoothstep(0.,0.03, -sdEquilateralTriangle(r-vec2(0.,-0.1),0.15));

    col=mix(col, eyes, EYE);
    col=mix(col, eyes, EYE2);

    col = mix(col, eyes, smoothstep(0.,0.03, -sdStairs(rMirrorX-vec2(-0.1,-0.55),vec2(0.18, 0.1),4.0)));


    fragColor=vec4(col,1.0);

}
