// animated vortices variant of https://shadertoy.com/view/cdfGz7

void mainImage( out vec4 O, vec2 u )  {  
    O = sqrt (1. - exp( - T(u) *vec4(1,2,4,1) ) ); 
}

// ---- compute BBoxs of next curves

void mainImage( out vec4 O, vec2 u )
{
    R = iResolution.xy;
    float i = u.x-.5, l = 9., p = 1./R.y;

    if (u.y==1.5)
        if (iFrame==0)  // O = H(i).xyxy;
                           O = mix( .5*R/R.y, H(i).xy, .7 ).xyxy;
        else _i=i, O=T(u), O = vec4(O.zw,mod(O.zw+field(O.zw,iChannel0),R/R.y));

    if (u.y==.5) {
        vec2  P = H(i+.5-iTime), _P=P, m=P, M=P; // random start point

        for( int t=0; t <len; t++, _P = P )      // follow trajectory
            P += field(P,iChannel0),
            m = min(m,P), M = max(M,P);        

        O = vec4(m-p,M+p);
    }
}

int len = 20; // curve length ( i.e. number of samples )
vec2 R;
#define T(U)   texelFetch( iChannel0, ivec2(U), 0 )
#define W(i)   texelFetch( CH, ivec2(i,1), 0 ).xy
#define H(p) ( fract(sin(float(p)*vec2(269.5,183.3)) *43758.5453123)*R/R.y )

float L(vec2 p, vec2 a,vec2 b) { // --- draw line  ( squared distance )
    p -= a, b -= a;
    float h = dot(p, b) / dot(b, b), 
          c = clamp(h, 0., 1.);     
    return h==c ? dot(p -= b*h ,p) : 1e5; 
}

// WHY the HELL can't we use iChannel in Commons ?!?!?!?

float _i = -1.;
vec2 field(vec2 U,sampler2D CH) {    // --- velocity field induced by vortices ( Biot & Savart )
    vec2 V = vec2(0), P;
    for( float i=0.; i < 30.; i++ ){ // N random vortices ( same strength )
        if (i==_i) continue;         // to avoid singularity for vorticies
        P = W(i) - U;
     // if (P==vec2(0)) continue;    // to avoid singularity for vorticies
        V += vec2(-P.y,P.x)  * min(100.,.1/pow(length(P),3.)) 
                     // * sign(H(i+.3).y-.5)  // random sign
                     // * (2.*H(i+.3).y-1.)   // random sign & strength
                     // * H(i+.3).y*2.        // random strength
                     ;
                          // * min(100.,.2/dot(P,P) );
    }
    return V*1e-3;
}

// --- draw only curves which BBox covers the pixel

void mainImage( out vec4 O, vec2 u )
{
     R = iResolution.xy;
     vec2 U = u / R.y, P, _P, b;
    O =  .9*T(u);                           // cumulates previous draw

    for( float i=0.,l; i < 400.; i++ ) {    // draw N streamlines per frame
        vec4 B = texelFetch(iChannel1,ivec2(i,0),0); // curve bbox
        b = step(B.xy,U)*step(U,B.zw);
     // if (b.x*b.y == 0.) continue;        // ← this form crash Firefox !
        if (b.x*b.y == 1.) {                // pixel not in BBox: skip draw
          _P = P = H(i+.5-iTime);           // random start point
        l = 9.;
        for( int t=0; t <len; t++, _P = P ) // follow trajectory
            P += field(P,iChannel1),
            l = min( l, L(U,_P,P) );
        O += .2*max(0., 1.-R.y*sqrt(l));     // draw the curve
    }}
}
