#define PI  3.141592653589
#define TAU PI * 2.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.y - 0.5);

    vec3 col = vec3(0.0);


    float t0 = mod(iTime * 0.25, 1.0);
    float t1 = smoothstep(0.0, 1.0, t0);
    float t2 = t0 * t0;
    float t3 = t0 * t0 * t0;
    float t4 = cos(t0 * PI / 2.0 - PI);
    float t5 = sqrt(t0);

    vec2 p0 = vec2(sin(t0 * TAU), cos(t0 * TAU)) * .05;
    vec2 p1 = vec2(sin(t1 * TAU), cos(t1 * TAU)) * .1;
    vec2 p2 = vec2(sin(t2 * TAU), cos(t2 * TAU)) * .15;
    vec2 p3 = vec2(sin(t3 * TAU), cos(t3 * TAU)) * .2;
    vec2 p4 = vec2(sin(t4 * TAU), cos(t4 * TAU)) * .25;
    vec2 p5 = vec2(sin(t5 * TAU), cos(t5 * TAU)) * .3;

    col += step(length(uv), .02) * vec3(1,1,1);
    col += step(length(p0 - uv), .02) * vec3(1,0,0);
    col += step(length(p1 - uv), .02) * vec3(0,1,0);
    col += step(length(p2 - uv), .02) * vec3(0,0,1);
    col += step(length(p3 - uv), .02) * vec3(1,0,1);
    col += step(length(p4 - uv), .02) * vec3(0,1,1);
    col += step(length(p5 - uv), .02) * vec3(1,1,0);
    col += step(abs(uv.x), .002) * step(0., uv.y) * vec3(1,1,1);

    fragColor = vec4(col,1.0);
}
