void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float W = 1. / 2.;

    // Output to screen
    fragColor = vec4(
                     createRect(uv, vec2(0,0),           vec2(W,W), col)
                   + createRect(uv, vec2(1. - W,1. - W), vec2(W,W), abs(cos(col)))
                   + createRect(uv, vec2(1. - W,0),      vec2(W,W), 0.5 + 0.25 * abs(sin(tan(4. * col))))
                   + createRect(uv, vec2(0,1. - W),      vec2(W,W), abs(fract(1. - col))),1.0);
}

vec3 createRect(vec2 uv, vec2 position, vec2 size, vec3 color) {
    if(uv.x > position.x && uv.x < position.x + size.x && uv.y > position.y && uv.y < position.y + size.y) {
        return color;
    }
    return vec3(0,0,0);
}
vec3 createCircle(vec2 uv, vec2 position, float radius, vec3 color) {
    float x = uv.x - position.x - pow(uv.x - position.x, 2.);
    if(x > .0) {
        if(uv.y < sqrt(x) && uv.y < sqrt(x)) {
            return color;
        }
    }
    return vec3(0,0,0);
}
