float d(in float x) {
    return x - (x * x);
}
vec3 r(in vec3 x) {
    vec3 g = x - 0.1;
    return vec3(d(g.x * 2.0), d(g.y * 2.), d(g.z * 2.)) / 2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = vec4(vec3(1.1, 1.1, 1.1) * vec3(1.05,iFrameRate / 60.,iFrameRate / 60.) * sqrt(r((uv.y - abs(tanh((mod(iTime, 42.)))))
      + vec3(1,1,1) / abs(tan(d(iTime + uv.x))))), 1.);
}
