vec3 palette(float t) {
    vec3 a = vec3(sin(iTime), 0.500, 1.608);
    vec3 b = vec3(0.918, cos(iTime), -0.862);
    vec3 c = vec3(-1.691, 0.660, 0.660);
    vec3 d = vec3(-2.312, -1.082, 0.667);
    return a + b*cos(6.28318*(c*t+d));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.);

    for (float i =0.; i< 3.; i++){
        uv = fract(uv*1.4) - .5;
        float d = length(uv)*exp(length(uv0*.5));
        vec3 col = palette(length(uv0)+i*1.5 + sin(iTime)*.3);
        d=sin(d* 8. + iTime)/8.;
        d = abs(d);
        d =pow( 0.01/d,1.2);

        finalColor += col *d;    
    }


    fragColor = vec4(finalColor,1.0);
}
