uint sobol(uint index, uint dim) {
    uint X = 0u;
    for (uint bit = 0u; bit < 32u; bit++) {
      uint mask = (index >> bit) & 1u;
      X ^= mask * directions[dim*32u + bit];
    }
    return X;
}

uvec2 sobol2( uint i ) {
  return uvec2(sobol(i, 0u), sobol(i, 1u));
}
vec2 sobol2f( uint i ) {
  return vec2(sobol(i, 0u), sobol(i, 1u)) *(1.0 / float(0xffffffffu));
}


// https://psychopath.io/post/2021_01_30_building_a_better_lk_hash
uint laine_karras_permutation(uint x, uint seed) {
#if 1
	x ^= x * 0x3d20adeau;
	x += seed;
	x *= (seed >> 16u) | 1u;
	x ^= x * 0x05526c56u;
	x ^= x * 0x53a22864u;
#else
    x += seed;
    x ^= x * 0x6c50b47cu;
    x ^= x * 0xb82f1e52u;
    x ^= x * 0xc7afe638u;
    x ^= x * 0x8d22f6e6u;
#endif
    return x;
}

uint hash_combine(uint seed, uint v) {
    return seed ^ (v + (seed << 6u) + (seed >> 2u));
}

uint nested_uniform_scramble(uint x, uint seed) {
	x = reverse_bits(x);
    x = laine_karras_permutation(x, seed);
    x = reverse_bits(x);
    return x;
}


vec2 sso2d(uint dim1, uint index) {

	index = nested_uniform_scramble(index, seedd);

	uint x = sobol(index, dim1);

	x = nested_uniform_scramble(x, hash_combine(seedd, dim1));

	uint y = sobol(index, dim1+1u);

	y = nested_uniform_scramble(y, hash_combine(seedd, dim1+1u));


	return vec2(x, y)* (1.0 / float(0xffffffffu));
}






vec3 rayPointAt(Ray ray, float t) {
 	return ray.origin + t * ray.direction;
}



// Duff et al. 2017. "Building an Orthonormal Basis, Revisited"
void make_orthonormal(vec3 n, out vec3 b1, out vec3 b2) {
	float sign = (n.z >= 0.0) ? 1.0 : -1.0;
	float a = -1.0 / (sign + n.z);
	float b = n.x * n.y * a;
	b1 = vec3(1.0 + sign * n.x * n.x * a, sign * b, -sign * n.x);
	b2 = vec3(b, sign + n.y * n.y * a, -n.y);
}



vec3 sample_hemisphere_cosine(vec3 N, vec2 xi) {
    // safe_sqrt
	float phi = xi.x * M_2PI;
	float cos_theta = sqrt(xi.y);
	float sin_theta = sqrt(1.0 - cos_theta*cos_theta);

	return vec3(sin_theta * cos(phi), sin_theta * sin(phi), cos_theta);
}



vec3 bsdf_sample_lambert(vec3 albedo, vec3 N, vec2 r, out vec3 L, out float pdf) {
    vec3 wo = sample_hemisphere_cosine(N, r);

    vec3 X, Y;
    make_orthonormal(N, X, Y);

    L = X * wo.x + Y * wo.y + N * wo.z;
    pdf = wo.z * M_1_PI;

    return albedo*pdf;
}

vec3 bsdf_eval_lambert(vec3 albedo, vec3 N, vec3 L, out float pdf) {

	pdf = dot(N, L) * M_1_PI;
	if(pdf < 1e-6) { 
        pdf = 0.0; 
        return vec3(0);
    }

	return albedo*pdf;
}








/*
    to increase metal's reality, replace this to fresnel which support complex refraction index.
    to increase performance, replace to schlick approx. there is many varient.
*/
vec3 fresnel_dielectric_color(vec3 color, float cosNV) {
  vec3 colorsqrt = sqrt(clamp(color, vec3(0.0, 0.0, 0.0), vec3(0.999)));
  vec3 n = (1.0 + colorsqrt) / (1.0 - colorsqrt);
  vec3 g = sqrt(n*n + cosNV*cosNV - 1.0);
  vec3 a = (g - cosNV) / (g + cosNV);
  vec3 b = ((g + cosNV) * cosNV - 1.0) / ((g - cosNV) * cosNV + 1.0);

  return 0.5 * a*a * (1.0 + b*b);
}


/*
    PDF Approximation from Blender Cycles engine.
    https://github.com/blender/blender/blob/v3.5.0/intern/cycles/kernel/closure/bsdf_microfacet_multi.h

    SPDX-License-Identifier: Apache-2.0 
    Copyright 2011-2022 Blender Foundation

*/
float multipleGGX_albedo(float alpha_xy) {

    float r = sqrt(alpha_xy);
    float albedo = 0.806495 * exp(-1.98712 * r*r) + 0.199531
    -((((((1.76741*r - 8.43891)*r + 15.784)*r - 14.398)*r
    + 6.45221)*r - 1.19722)*r + 0.027803)*r - 0.00568739;

    return saturate(albedo);
}



// Eric Heitz. 2018. "Sampling the GGX Distribution of Visible Normals"
// allow lower hemishere view
vec3 sample_D_wi(vec3 V, vec2 alpha, float xi0, float xi1)
{
	vec3 Vh = normalize(vec3(alpha.x * V.x, alpha.y * V.y, V.z));
	float lensq = Vh.x * Vh.x + Vh.y * Vh.y;
	vec3 T1 = lensq > 0.0 ? vec3(-Vh.y, Vh.x, 0.0) * inversesqrt(lensq) : vec3(1, 0, 0);
	vec3 T2 = cross(Vh, T1);
#if 1
	float r = xi0 > 0.0 ? sqrt(xi0) : 0.0;
	float phi = M_2PI * xi1;
	float t1 = r * cos(phi);
	float t2 = r * sin(phi);
#else
	vec2 disk = sample_disk_concentric(xi);
	float t1 = disk.x;
	float t2 = disk.y;
#endif
	float s = 0.5 * (1.0 + Vh.z);
	t2 = mix(sqrt(1.0 - t1*t1), t2, s);
	vec3 Nh = t1 * T1 + t2 * T2 + sqrt(max(0.0, 1.0 - t1*t1 - t2*t2)) * Vh;

	return normalize(vec3(alpha.x * Nh.x, alpha.y * Nh.y, max(0.0, Nh.z)));
}




vec3 bsdf_sample_ggx(Material m,
	 vec3 N, vec3 V, vec2 xi, out vec3 L, out float pdf)
{
    vec2 alpha = vec2(m.Roughness * m.Roughness);

    float cosNV = dot(N, V);

    if (cosNV < 1e-7) {
        pdf = 0.0;
        return vec3(0);
    }

    if (alpha.x <= 1e-3 && alpha.y <= 1e-3) {
        /* specular case. off MIS */
        L = 2.0 * N * cosNV - V;

        return fresnel_dielectric_color(m.Color, cosNV);
    }


    vec3 X, Y;
    make_orthonormal(N, X, Y);



    vec3 wi = vec3(dot(X, V), dot(Y, V), cosNV);
    vec3 wm = sample_D_wi(wi, alpha, xi.x, xi.y);
    float cosVH = dot(wi, wm);
    L = 2.0 * wm * cosVH - wi;

    float cosNL = L.z;
    L = normalize(X * L.x + Y * L.y + N * L.z);

    if (cosNL < 1e-7) {
        pdf = 0.0;
        return vec3(0);
    }

    float cosNH = wm.z;


    float alpha2 = alpha.x * alpha.x;
    float d = (cosNH * alpha2 - cosNH) * cosNH + 1.0;
    float D = M_1_PI * alpha2 / (d * d);

    float termL = sqrt(cosNL * (cosNL - cosNL * alpha2) + alpha2);
    float termV = sqrt(cosNV * (cosNV - cosNV * alpha2) + alpha2);
    float G2_by_cosNV = 2.0 * cosNL / (cosNV*termL + cosNL*termV);
    float G1v_by_cosNV = 2.0 / (cosNV + termV);


    vec3 F = fresnel_dielectric_color(m.Color, cosVH);


    pdf = 0.25 *D* G1v_by_cosNV;

    return 0.25 * D *F * G2_by_cosNV;
}

vec3 bsdf_eval_ggx(Material m,
	 vec3 N, vec3 V, vec3 L, out float pdf) {

    vec2 alpha = vec2(m.Roughness * m.Roughness);

    float cosNV = dot(N, V);

    if (cosNV < 1e-7) {
        pdf = 0.0;
        return vec3(0);
    }

    if (alpha.x <= 1e-3 && alpha.y <= 1e-3) {
        /* specular case. off MIS */

        pdf = 0.0;
        return vec3(0);
    }

    vec3 H = normalize(V + L);
    float cosVH = dot(V, H);

    float cosNL = dot(N, L);

    if (cosNL < 1e-7) {

        pdf = 0.0;
        return vec3(0);
    }

    float cosNH = dot(N, H);


    float alpha2 = alpha.x * alpha.y;
    float d = (cosNH * alpha2 - cosNH) * cosNH + 1.0;
    float D = M_1_PI * alpha2 / (d * d);

    float termL = sqrt(cosNL * (cosNL - cosNL * alpha2) + alpha2);
    float termV = sqrt(cosNV * (cosNV - cosNV * alpha2) + alpha2);
    float G2_by_cosNV = 2.0 * cosNL / (cosNV*termL + cosNL*termV);
    float G1v_by_cosNV = 2.0 / (cosNV + termV);


    vec3 F = fresnel_dielectric_color(m.Color, cosVH);

    pdf = 0.25 * D * G1v_by_cosNV;

    return 0.25 * D *F * G2_by_cosNV;
}


/*
    see supplment for G1_dist, lambda(), phase
    Heitz et al. 2016. "Multiple-Scattering Microfacet BSDFs with the Smith Model"



*/


/*
    smith lambda of ggx.  sign(wi.z) is important.

    lambda(-wr) = 1 + lambda(wr) 
*/

float Lambda(vec2 alpha, vec3 wi) {
    vec2 wa = wi.xy * alpha / max(abs(wi.z), 1e-6);

    return 0.5 * (-1.0 + sign(wi.z) * sqrt(1.0 + dot(wa, wa)));
}


vec3 bsdf_sample_ggx_multi( Material m, vec3 N, vec3 V, out vec3 L, out float pdf)
{
    vec2 alpha = vec2( m.Roughness * m.Roughness);

    vec3 X, Y;
    make_orthonormal(N, X, Y);
    vec3 wi = (vec3(dot(X, V), dot(Y, V), dot(N, V)));
    vec3 wr = -wi;

    bool escape = false;
    vec3 eval = vec3(1.0);
    float z = 0.0;

#if SS_STOCHASTIC_SINGLE
    for (int i = 0; i < 2; i++) {
#else
    for (int i = 0; i < 10; i++) {
#endif
        float lambda_wr = Lambda(alpha, wr);
        float xi = random();

        z += -log(xi) / lambda_wr;

        if (z > 0.0) {
            escape = true;
            break;
        }

        if (i < 9) {
            float xi0 = random();
            float xi1 = random();
            vec3 wm = sample_D_wi(-wr, alpha, xi0, xi1);

            float cosVH = dot(-wr, wm);
            wr = normalize(2.0 * wm * cosVH + wr);
#ifndef SS_WHITE_FURNACE_TEST
            eval *= fresnel_dielectric_color(m.Color, cosVH);
#endif
        }
    }


    if (!escape || wr.z < 1e-7) {
        //eval = single;
        pdf = 0.0;
        return eval*0.0;
    }

    L = X * wr.x + Y * wr.y + N * wr.z;

    float pdf_single;
    float alpha2 = alpha.x * alpha.y;
    {
        vec3 wm = normalize(wi+wr); 
        float d = (wm.z * alpha2 - wm.z) * wm.z + 1.0;
        float D = M_1_PI * alpha2 / (d * d);

        float termV = sqrt(wi.z * (wi.z - wi.z * alpha2) + alpha2);

        float G1v_by_dotNV = 2.0 / (wi.z + termV);

        pdf_single = 0.25 * D * G1v_by_dotNV;
    }

#if SS_STOCHASTIC_SINGLE
    float ratio = 1.0;
#else
    float ratio = multipleGGX_albedo(alpha2);
#endif
    float pdf_multi = wr.z * M_1_PI;

    pdf = mix(pdf_multi, pdf_single, ratio);


    return eval * pdf;
}



vec3 bsdf_eval_ggx_multi(Material m, vec3 N, vec3 V, vec3 L, out float pdf) {

    vec2 alpha = vec2(m.Roughness * m.Roughness);

    vec3 X, Y;
    make_orthonormal(N, X, Y);
    vec3 wi = normalize(vec3(dot(X, V), dot(Y, V), dot(N, V)));
    vec3 wo = normalize(vec3(dot(X, L), dot(Y, L), dot(N, L)));

    // reduce variance
    bool swap = false;
    if (wo.z < wi.z) {
        swap = true;
        vec3 tmp = wi;
        wi = wo;
        wo = tmp;
    }
    vec3 wr = -wi;

    float z = 0.0;

    float pdf_single = 0.0;
    vec3 eval = vec3(0.0);
    vec3 throughput = vec3(1.0);

    // analytic single scattering 
    float alpha2 = alpha.x * alpha.y;
    {
#if SS_STOCHASTIC_SINGLE != 1 || SS_EVAL_ALALYTIC_SINGLE == 1
        vec3 wm = normalize(wi + wo);
        float d = (wm.z * alpha2 - wm.z) * wm.z + 1.0;
        float D = M_1_PI * alpha2 / (d * d);

        float termL = sqrt(wo.z * (wo.z - wo.z * alpha2) + alpha2);
        float termV = sqrt(wi.z * (wi.z - wi.z * alpha2) + alpha2);

        float G2_by_cosNV = 2.0 * wo.z / (wi.z*termL + wo.z*termV);
        float G1v_by_cosNV = 2.0 / (swap ? (wo.z + termL) : (wi.z + termV));

        pdf_single = 0.25 * D * G1v_by_cosNV;

        eval = fresnel_dielectric_color(m.Color, dot(wi, wm)) *
          0.25 * D * G2_by_cosNV;
#endif
    }


    /*
        From supplement material of 
        d'Eon et al. 2023."Microfacet theory for non-uniform heightfields"

        Algoritm 1
        tau = z * projectedarea(wo)
        result = result + exp(tau) * phase(wr, wo)

        seems optical depth should project back to wo by dividing wo.z


        G1l = exp((z / wo.z) * projectedarea(wo))
            = exp((z / wo.z) * wo.z * lambda(wo))
            = exp(z * lambda(wo))

        wo.z will cancelout and match with below result of G1l
    */
    float lambda_wo = Lambda(alpha, wo); 
    vec3 wm = normalize(-wr + wo);

#if SS_STOCHASTIC_SINGLE
    for (int i = 0; i < 1; i++) {
#else
    for (int i = 0; i < 10; i++) {
#endif
        float lambda_wr = Lambda(alpha, wr);
        float xi = random();

        z += -log(xi) / lambda_wr;
        if (z > 0.0) {
            break;
        }

#if SS_STOCHASTIC_SINGLE != 1 || SS_EVAL_ALALYTIC_SINGLE == 1
        // not estimate first bounce
        if (i > 0)
#endif
        {
            vec3 wm = normalize(-wr + wo);
            if (wm.z > 0.0) {

            float d = (wm.z * alpha2 - wm.z) * wm.z + 1.0;
            float D = M_1_PI * alpha2 / (d * d);

            vec3 F = fresnel_dielectric_color(m.Color, dot(-wr, wm));
            /*
                see this for z = log(C1(h))
                Dupuy et al. 2016. "Additional Progress Towards the Unification of Microfacet and Microflake Theories"

                z = log(C1(h))
                G1_dist(wr, h) = C1(h)^lambda(wr) = exp(z * lambda(wr))
            */
            float G1l = exp(z * lambda_wo);
            /*
                ray traversal toward wr, wo
                projectedarea(wr) = wr.z*lambda(wr)   // wr, wo
                projectedarea(-wr) = wr.z*(1 + lambda(wr)) // wi (which denote 'incident direction')


                wi = -wr
                projectedarea(wi) = wi.z * (1 + lambda(wi))
                projectedarea(wr) = wr.z * lambda(wr)
            */
            //float inv_projectedarea = 1.0 / max(1e-6, wr.z * lambda_wr);
            float inv_projectedarea = 1.0 / max(1e-6, -wr.z *(1.0+ Lambda(alpha , -wr)));
            /*  
                D_vis(wi, wm) = D(wi, wm) * max(0, dot(wi, wm)) / projectedarea(wi)
                phase(wi, wo) = D_vis(wi, wm) / (4 * abs(dot(wi, wm)))               // here, dot(wi, wm) > 0
                              = D(wi, wm) / (4 * projectedarea(wi))
            */
            eval += throughput * // energy
                    F * // absortion
                    G1l * // shadowing
                    0.25 * D *inv_projectedarea; // phase
            }
        }

        if (i < 9) {
            float xi0 = random();
            float xi1 = random();
            vec3 wm_ = sample_D_wi(-wr, alpha, xi0, xi1);

            float cosVH = dot(-wr, wm_);
            wr = normalize(2.0 * wm_ * cosVH + wr);



            throughput *= fresnel_dielectric_color(m.Color, cosVH);
        }
    }
#if SS_STOCHASTIC_SINGLE
    float ratio = 1.0;
#else
    float ratio = multipleGGX_albedo(alpha2);
#endif
// swap?
    float pdf_multi = (swap? wi.z :wo.z) * M_1_PI;

    pdf = mix(pdf_multi, pdf_single, ratio);

    eval *= swap ? abs(wi.z / wo.z) : 1.0;

    return eval;
}







// d'Eon et al. 2023."Microfacet theory for non-uniform heightfields"
vec3 bsdf_sample_ggx_multi_unsym(Material m, vec3 N, vec3 V, out vec3 L, out float pdf)
{
    float w_a = 0.5;
    float w_b = 1.0 - w_a;
    vec2 alpha_a = vec2(0.424*0.424);
    vec2 alpha_b = vec2(0.05*0.05);
    //vec3 color_a = vec3(1.0);
    //vec3 color_b = vec3(0.28, 0.88, 0.9);

    vec3 X, Y;
    make_orthonormal(N, X, Y);
    vec3 wi = (vec3(dot(X, V), dot(Y, V), dot(N, V)));
    vec3 wr = -wi;

    bool escape = false;
    vec3 eval = vec3(1.0);
    float z = 0.0;
    float zs = log(1.0 - w_a);

#if SS_STOCHASTIC_SINGLE
    for (int i = 0; i < 2; i++) {
#else
    for (int i = 0; i < 10; i++) {
#endif
        vec2 alpha =  z > zs ? alpha_a : alpha_b;
        float A = Lambda(alpha, wr);
        float xi = random();

        z += -log(xi) / A;



        if (z > 0.0) {
            escape = true;
            break;
        }

        if (i < 9) {
            vec2 alpha =  z > zs ? alpha_a : alpha_b;

            float xi0 = random();
            float xi1 = random();
            vec3 wm = sample_D_wi(-wr, alpha, xi0, xi1);

            float cosVH = dot(-wr, wm);
            wr = normalize(2.0 * wm * cosVH + wr);

            eval *= fresnel_dielectric_color(m.Color, cosVH);
        }
    }


    if (!escape || wr.z < 1e-7) {
        //eval = single;
        pdf = 0.0;
        return eval*0.0;
    }

    L = X * wr.x + Y * wr.y + N * wr.z;


    //todo: single scattering
    pdf = 1.0;


    return eval * pdf;
}




