//fast inverse for orthogonal matrices
mat4 mat4Inverse( in mat4 m ) {
    mat3 rotate_inv = mat3(	vec3( m[0][0], m[1][0], m[2][0] ),
                          	vec3( m[0][1], m[1][1], m[2][1] ),
                          	vec3( m[0][2], m[1][2], m[2][2] ) );

    return mat4(	vec4( rotate_inv[0], 0.0 ),
                	vec4( rotate_inv[1], 0.0 ),
                	vec4( rotate_inv[2], 0.0 ),
              		vec4( (-rotate_inv)*m[3].xyz, 1.0 ) );
}

vec3 toVec3( vec4 v ) {
    if( ( abs(v.w)<1e-4 ) ) {
        return v.xyz;
    }

    return v.xyz*(1.0/v.w);
}

// UreÃ±a et al. 2013. "An Area-Preserving Parametrization for Spherical Rectangles"
struct SphQuad {
    vec3 o, x, y, z; // local reference system R
    float z0, z0sq; //
    float x0, y0, y0sq; // rectangle coords in R
    float x1, y1, y1sq; //
    float b0, b1, b0sq, k; // misc precomputed constants
    float S; // solid angle of Q
};


void SphQuadInit(vec3 s,vec3 ex,vec3 ey,vec3 o, out SphQuad squad) {
    squad.o = o;
    float exl = length(ex), eyl = length(ey);
    // compute local reference system R
    squad.x = ex / exl;
    squad.y = ey / eyl;
    squad.z = cross(squad.x, squad.y);
    // compute rectangle coords in local reference system
    vec3 d = s - o;
    squad.z0 = dot(d, squad.z);
    // flip z to make it point against Q
    if (squad.z0 > 0.) {
    squad.z *= -1.;
    squad.z0 *= -1.;
    }
    squad.z0sq = squad.z0 * squad.z0;
    squad.x0 = dot(d, squad.x);
    squad.y0 = dot(d, squad.y);
    squad.x1 = squad.x0 + exl;
    squad.y1 = squad.y0 + eyl;
    squad.y0sq = squad.y0 * squad.y0;
    squad.y1sq = squad.y1 * squad.y1;
  // compute normals to edges
  float n0z = -squad.y0 * inversesqrt(squad.z0sq + squad.y0sq);
  float n1z = squad.x1 * inversesqrt(squad.z0sq + squad.x1*squad.x1);
  float n2z = squad.y1 * inversesqrt(squad.z0sq + squad.y1sq);
  float n3z = -squad.x0 * inversesqrt(squad.z0sq + squad.x0*squad.x0);
  // compute internal angles (gamma_i)
  float g0 = acos(-n0z * n1z);
  float g1 = acos(-n1z * n2z);
  float g2 = acos(-n2z * n3z);
  float g3 = acos(-n3z * n0z);
    // compute predefined constants
    squad.b0 = n0z;
    squad.b1 = n2z;
    squad.b0sq = squad.b0 * squad.b0;
    squad.k = M_2PI - g2 - g3;
    // compute solid angle from internal angles
    squad.S = g0 + g1 - squad.k;
}

void SphQuadSample(in vec3 x, SphQuad squad, float u, float v, out vec3 w, out float pdf) {
    // 1. compute cu
    float au = u * squad.S + squad.k;
    float fu = (cos(au) * squad.b0 - squad.b1) / sin(au);
    float cu = inversesqrt(fu*fu + squad.b0sq) * (fu>0. ? +1. : -1.);
    cu = clamp(cu, -1., 1.); // avoid NaNs
    // 2. compute xu
    float xu = -(cu * squad.z0) * inversesqrt(1. - cu*cu);
    xu = clamp(xu, squad.x0, squad.x1); // avoid Infs
    // 3. compute yv
    float d = sqrt(xu*xu + squad.z0sq);
    float h0 = squad.y0 * inversesqrt(d*d + squad.y0sq);
    float h1 = squad.y1 * inversesqrt(d*d + squad.y1sq);
    float hv = h0 + v * (h1-h0), hv2 = hv*hv;
    float yv = (hv2 < 1.-1e-4) ? (hv*d)* inversesqrt(1.-hv2) : squad.y1;
    // 4. transform (xu,yv,z0) to world coords

    vec3 p = (squad.o + xu*squad.x + yv*squad.y + squad.z0*squad.z);
    w = normalize(p - x);
    pdf = 1. / max(1e-6,squad.S);
}




bool hitScene(vec3 P, vec3 D, float tMin, float tMax,
              out float dist, out vec3 normal, out Material material)
{
    float near = tMax;
    bool hit = false;

    for (int i = 0; i < NUM_SPHERES; i++) {
        Sphere sphere = sphere[i];

        vec3 f = P - sphere.center;
        float b = dot(-f, D);
        vec3 k =  f + b * D;
        float r2 = sphere.radius * sphere.radius;
        float k2 = dot(k, k);

        if (r2 >= k2) {
            /* drop sign(b) from (sign(b) * sqrt(r2 - k2)) to handle center behind ray */
            float q = b + sqrt(r2 - k2);
            float f2 = dot(f,f);
            float t = f2 < r2 ?  q : (f2 - r2) / q;
            if (t > tMin && t < near) {
                near = t;
                hit = true;
                normal = (P+D*t - sphere.center) / sphere.radius;
                material = sphere.material;
                if (mod(normal.z, 0.3) < 0.15)
                //    material.Roughness = 0.6;
                //else
                    material.Roughness *= 0.75;
            }
        }
    }
#ifndef SS_WHITE_FURNACE_TEST
    for (int j = 0; j < NUM_PLANES; j++) {
        Plane plane = plane[j];
        float cosNV = dot(D, -plane.normal);
        vec3 axis = P * abs(plane.normal);
        float a = axis.x + axis.y + axis.z;

        float t = ( a - plane.pos ) / cosNV;
        if ( t > tMin && t < near ) {
            near = t;
            hit = true;
            vec3 position = P+D*t;
            normal = plane.normal * sign(cosNV);
            material = plane.material;
            if ((mod(position.x, 1.0) > 0.5 && mod(position.y, 1.0) > 0.5) || (mod(position.x, 1.0) < 0.5 && mod(position.y, 1.0) < 0.5))
                material.Color = vec3(0.7177);
            else
                material.Color = vec3(0.22);
        }
    }

#    ifdef SS_AREA
    for (int i = 0; i < NUM_RECTS; i++) {
        Rect rect = rect[i];
        float a = rect.k - P.z;
        float t = a / D.z;

        if (t > tMin && t < near) {
            float x = P.x + t * D.x;
            float y = P.y + t * D.y;

            if (x > rect.x0 && x < rect.x1 && y > rect.y0 && y < rect.y1) {
                near = t;
                hit = true;
                normal = vec3(0.0, 0.0 ,-sign(a));
                material = rect.material;
            }
        }
    }
#    endif
#endif

    dist = near;
 	return hit;
}







