// #define SS_HAZY



int spilt;

vec3 trace(Ray ray) {

	vec3 N;
	Material material;

	vec3 radiance = vec3(0.0);
	vec3 throughput = vec3(1.0);


	vec3 eval = vec3(1.0);
	float pdf = 0.0;

    for (int b = max(-iFrame, 0); ;) {

        float dist;
        if (!hitScene(ray.origin, ray.direction, 1e-5, 1e+9, dist, N, material)) {
#ifdef SS_WHITE_FURNACE_TEST        
            vec3 sky = vec3(0.5);
#else
            vec3 sky = vec3(0.0);
#    ifdef SS_SKY   
            sky = HDR(texture(iChannel2, ray.direction).xyz);
#    endif
#endif
            radiance += throughput * sky;
            break;
        }
        vec3 position = ray.origin + ray.direction*dist;
        vec3 V = -ray.direction;
        vec3 L;
        vec3 Ng = N;

        vec2 r = sso2d(uint(2+b*2), sam);

        //r = vec2(random(), random());

        switch(material.type) {

            case LIGHT :
            {
                if(pdf == 0.) return radiance;

                float mis = 1.0;
#if defined(SS_MIS) 
                // calc solid angle for MIS
                const mat4 trans = mat4(
                vec4( 1.0, 0.0, 0.0, 0.0 ),
                vec4( 0.0, 1.0, 0.0, 0.0 ),
                vec4( 0.0, 0.0, 1.0, 0.0 ),
                vec4( 0.0, 0.0, 12.0, 1.0 ));

                mat4 itrans = mat4Inverse(trans);
                vec3 pos =  toVec3(itrans*vec4(ray.origin,1));
                vec2 pmin = vec2(-2.) , pmax = vec2(2.);
                vec3 s = vec3(pmin, 0.0);
                vec3 ex = vec3(pmax.x - pmin.x, 0., 0.);
                vec3 ey = vec3(0., pmax.y - pmin.y, 0.);
                SphQuad squad;
                SphQuadInit(s, ex, ey, pos, squad);

                // actually we need only solidangle, so this is unnecessary..
                vec3 Li;
                float pdfl;
                vec2 xi0;// = vec2(random(), random());

                SphQuadSample(pos, squad, xi0.x, xi0.y, Li, pdfl);

                mis = pdf*pdf/(pdfl*pdfl+pdf*pdf);

#endif 

#if defined(SS_BSDF) || defined(SS_MIS) 
                radiance += throughput* material.Color * mis;
#endif  
                return radiance;

                break;
            }


            case DIFFUSE :
            {
                eval = bsdf_sample_lambert( material.Color, N, r, L, pdf);
                break;
            }


            case GLOSSY :
            {
                if(spilt == 0)
                    eval= bsdf_sample_ggx_multi(material, N, V, L, pdf);
                else
#ifndef SS_HAZY
                    eval = bsdf_sample_ggx(material, N, V, r, L , pdf);
#else
                    eval = bsdf_sample_ggx_multi_unsym(material, N, V, L , pdf);
#endif

                break;
            }


            default :
            {
                radiance = vec3(0);
                break;
            }
        }

        if (b >= max(-iFrame, SS_MAX_BOUNCES)) {
            break;
        }

        b++;


       ray = Ray(position+N*0.0001, normalize(L));

#if defined(SS_LIGHT) || defined(SS_MIS)      
        //  NEE
        // sample rect light (solid angle)

        const mat4 trans = mat4(	
        vec4( 1.0, 0.0, 0.0, 0.0 ),
        vec4( 0.0, 1.0, 0.0, 0.0 ),
        vec4( 0.0, 0.0, 1.0, 0.0 ),
        vec4( 0.0, 0.0, 12., 1.0 ));

        mat4 itrans = mat4Inverse(trans);
        vec3 pos =  toVec3(itrans*vec4(position,1));

        vec2 pmin = vec2(-2.) , pmax = vec2(2.);
        vec3 s = vec3(pmin, 0.0);
        vec3 ex = vec3(pmax.x - pmin.x, 0., 0.);
        vec3 ey = vec3(0., pmax.y - pmin.y, 0.);
        SphQuad squad;
        SphQuadInit(s, ex, ey, pos, squad);

        vec3 Li, du;
        float pdfl;
        vec2 xi0 = vec2(random(), random());


        SphQuadSample(pos, squad, xi0.x, xi0.y, Li, pdfl);
        Material tmp;

        L = toVec3(trans*vec4(Li,0));

        if(dot(L,N)>0.){
            float dis;
            if (hitScene(position, L, 0.0001, 1e+9, dis, du, tmp)) {
                if(tmp.type==LIGHT) {

                vec3 evalb;
                float pdfb;

                switch(material.type) {


                    case DIFFUSE :
                    {
                        evalb = bsdf_eval_lambert(material.Color, N, L, pdfb);
                        break;
                    }


                    case GLOSSY :
                    {
                        if(spilt == 0)
                            evalb = bsdf_eval_ggx_multi(material, N, V, L, pdfb);

                        else
#ifndef SS_HAZY
                            evalb = bsdf_eval_ggx(material, N, V, L , pdfb);                      
#else
                            evalb = bsdf_eval_ggx_multi_unsym(material, N, V, L , pdfb);
#endif


                        break;
                    }
                }

                float mis = 1.0;
#    if defined(SS_MIS)                   
                mis = pdfl*pdfl/(pdfl*pdfl+pdfb*pdfb);
#    endif 

                radiance +=  throughput* (tmp.Color/pdfl) *evalb *mis;
                }
            }
        }
#endif // LIGHT || MIS

        throughput *= eval/pdf;

        if(dot(N, ray.direction) < 0.0 || pdf <= 0.0) return radiance;
    }


    return radiance;
}




void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 PreColor = texelFetch(iChannel0, ivec2(fragCoord), 0);


    if (PreColor.a >= SS_SAMPLE) {
        fragColor = PreColor;
        return;
    }


    PreColor.a++;

    // progress bar
    if (fragCoord.y > iResolution.y - 5.5) {
        float b = (uv.x < (PreColor.a) / SS_SAMPLE) ? 1.0 : 0.0;
        fragColor = (false) ?vec4(0) : PreColor.a * vec4(b, 0.0, b, 1.0);
        return;
    }



    initScene();

    // init global RNG
    vec3 bluenoise = texelFetch(iChannel1, ivec2(fragCoord) % ivec2(1024), 0).xyz;
    // seed1 = uint(1857371.0*IGN(gl_FragCoord.xy)) + 5392840U*uint(PreColor.a);
    //seed1 = reverse_bits(uint(gl_FragCoord.x + gl_FragCoord.y *iResolution.x))+ 9280U*uint(PreColor.a);
    seed1 = uint(1857371.0*IGN(gl_FragCoord.xy)) + 9280U*uint(PreColor.a * SS_SAMPLE_per_FRAME);
    seedd = uint(1857371.0*IGN(gl_FragCoord.xy));
    sam = uint(SS_SAMPLE_per_FRAME*PreColor.a);

    // screen split
    if(uv.x< 0.5)
        spilt = 0;
    else
    {
        spilt = 1;
        //sphere[0].material.type = DIFFUSE;
    }


    // camera
    vec3 origin;
    vec3 lookAt = vec3(0.0, 0.0, 1.0);

    #if 1
    origin.x = -3.055;
    origin.y = -3.055;
    origin.z = 2.837;
    #else
    const float dist = 1.0;
    vec2 mousePos = vec2(5.,1.)*(mouse.xy + vec2(0.05, -1.51));
    origin.x = sin(mousePos.y)* cos( mousePos.x ) * dist;
    origin.y = sin(mousePos.y)* sin( mousePos.x ) * dist;
    origin.z = cos(mousePos.y)* dist;
    #endif


    const float FOV = 50.0;
    const float halfWidth = tan(radians(FOV) * 0.5);
    float halfHeight = halfWidth * iResolution.y / iResolution.x;


    vec2 xi = vec2(0.5);
    //rand = hammersley( uint(PreColor.a), uint(AA_SAMPLE));
    //rand = halton2(int(PreColor.a), 2, 3);
    //rand.x = fract(bluenoise.x + rand.x);
    //rand = R2(vec2(0.5, 0.47), PreColor.a );?? unknown aliasing
    //rand = vec2(random(),random());
    xi = sso2d(0u, uint(PreColor.a));

    vec3 w = normalize(lookAt - origin);
    vec3 u = normalize(cross(w, vec3(0.0, 0.0, 1.0)));
    vec3 v = cross(u, w);

    vec2 uvc = (fragCoord + xi-0.5) / vec2(iResolution) * 2.0 - 1.0;
    vec3 D = vec3(uvc.x * halfWidth, uvc.y * halfHeight, 1.0);
    D = D.x*u + D.y*v + D.z*w;
    D = normalize(D);
    vec3 P = origin;   




    vec3 Color = vec3(0);
    for (int i = min(iFrame, 0); iFrame>-9&& i < max(-iFrame, int(SS_SAMPLE_per_FRAME)); ) {
        Color += trace(Ray(P, D));
        sam ++;
        i++;
    }
    Color /= SS_SAMPLE_per_FRAME;

    fragColor = (false) ? vec4(Color, 0.0)
    : vec4(PreColor.rgb*(PreColor.a==1.?0.:1.) + Color, PreColor.a);

}
