    vec3 deform( in vec2 p, in float t )
{
    t *= 2.0;

    p += 1.5*sin( t*vec2(10.1,1.3)+vec2(0.0,0.5) );

	float a = atan( p.y, p.x );
        float r = length( p );

           float s = r * (1.0+0.5*cos(t*1.7));

            vec2 uv = 0.0*t + 0.05*p.yx + 0.05*vec2( cos(t+a*2.0),  
                                             sin(t+a*2.0))/s;

            return texture( iChannel0, 0.5*uv ).xyz;
  }
            void mainImage( out vec4 fragColor, in vec2 fragCoord )
    {
            vec2 q = fragCoord/iResolution.xy;
            vec2 p = -1.0 + 2.0*q;

            vec3 col = vec3(0.0);
            for( int i=0; i<20; i++ )
    {
            float t = iTime + float(i)*0.0035; 
            col += deform( p, t );
  }
        col /= 20.0;

    col = pow( col, vec3(1.6,0.7,0.8) );

    fragColor = vec4( col, 1.0 );
}

float random (in vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix(mix(random(i + vec2(0.0,0.0)), 
                   random(i + vec2(1.0,0.0)), u.x),
               mix(random(i + vec2(0.0,1.0)), 
                   random(i + vec2(1.0,1.0)), u.x), u.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 st = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(1.0);

    st *= 1000.0;
    float n = 0.0;
    for (int i = 0; i < 5; i++) {
        n += noise(st);
        st *= 2.0;
    }
    color.r = sin(n * 2.0 * 3.14159) * 0.5 + 0.5;
    color.g = sin((n + 0.5) * 2.0 * 3.14159) * 0.5 + 0.5;
    color.b = sin((n + 1.0) * 2.0 * 3.14159) * 0.5 + 0.5;

    fragColor = vec4(color, 1.0);
}
