void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = vec4(0);
    fragColor.rgb += vec3(step(uv.y, f(uv.x*1.1)));

    if(uv.x>.9) fragColor.rgb = f(uv.y);
    fragColor = vec4(clamp(fragColor.xyz,0.,1.), 1.0);
}

vec3 start = vec3(.0, .1, .2);
vec3 middle = vec3(.5, .6, .7);
vec3 end = vec3(.9, 1., 1.1);
vec3 mul = vec3(1,1,.7);
vec3 add = vec3(0,0,0);

#define f(tt) (smoothstep(start,middle,vec3(tt)) - smoothstep(middle,end,vec3(tt))) * mul + add


vec4 vec( vec3 tt ) {
    return vec4(tt, 1.0);
}
