float Spot (vec2 uv, float x, float y, float i) {
    return smoothstep(.4/i, .0, length(uv-vec2(x,y))-.001);
    }

vec3 col = vec3(0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    for(float i = 1.; i < 256.; ++i)
  { float p=iTime-i*.003;
    col += vec3(2./i,.5/i,.5)* Spot(uv, (sin(p*4.)*cos(p*2.))/2., sin(p*3.)/3., i);
  }

    fragColor = vec4(col,1.);
}
