void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 p=-1.16+3.0 - fragCoord.xy / iResolution.xy;

  p.x /=0.5;
  p /=0.2;
  p.y +=iTime;
  p.y +=sin(0.1*p.y+iTime*1.0);
  p.x +=cos(0.5*p.x+iTime*1.0);
  p.y +=sin(1.5*p.x+iTime*1.0);
  p -=sin(p.y*atan(8.0));
  p +=cos(p.x*tan(6.5));
  p /=tan(2.18);
  p *=p*cos(p*p.x*0.8);
  p *=sin(p.x/32.0*sin(p.x*abs(2.0)/5000.0));

  vec3 col=vec3(p.x+0.4, p.x, p.x+0.8);
  vec3 col2=vec3(0.6, 1.0, 1.0);

  fragColor=vec4((col-sin(col)/12.0)*col2, 0.0);
}
