#define PI 3.14159
#define sinn(X) ((sin(X) + 1.0) * 0.5)
#define cosn(X) ((cos(X) + 1.0) * 0.5)

void mainImage(out vec4 frag_color, in vec2 frag_coord)
{
    vec2 uv = frag_coord;
    vec4 bg = vec4(sinn(iTime * 0.8), cosn(iTime * 0.2), cosn(iTime * 0.4), 1.0);
    vec2 center = iResolution.xy * 0.5;
    float radius = iResolution.y * 0.25 * (sinn(iTime * PI) * 0.4 + 0.6) * sinn(iTime + uv.x * 0.2);
    float dist = distance(uv, center) - radius;

    frag_color = mix(bg, 1.0 - bg, clamp(1.0 - dist, 0.0, 1.0));
}
