#define ID_GROUND 1
#define ID_BOX 2

float groundSDF(in vec3 p) {
    return p.y;
}

float sdf(inout Data data, in vec3 p) {
    int skip = data.skip;
    int id = ID_NONE;
    float dist = FAR;
    Object o = NEW_OBJECT;

    float ground = groundSDF(p);
    SAMPLE(ground, o, ID_GROUND);

    float box = boxSDF(p - vec3(0, 1, 0), vec3(1.0))-0.03;
    SAMPLE(box, o, ID_BOX);

    data.id = id;
    data.o = o;
    return dist;
}

float checkerTexture(in vec2 uv, in float s) {
    vec2 d = max(abs(dFdx(uv)), abs(dFdy(uv))) + s;
    vec2 t1 = vec2(1.-2.0*abs(fract((uv.x+.5*d.x)*0.5)-0.5), 1.-2.0*abs(fract((uv.y+.5*d.y)*.5)-0.5));
    vec2 t2 = vec2(1.-2.0*abs(fract((uv.x-.5*d.x)*0.5)-0.5), 1.-2.0*abs(fract((uv.y-.5*d.y)*.5)-0.5));
    return 0.5 - 0.5*((t1.x-t2.x)/d.x)*(t1.y-t2.y)/d.y;
}

vec3 getAlbedoGround(inout Data data) {
    vec3 p = data.p;
    vec3 n = data.n;
    vec2 uv = boxUv(p, n);
    uv -= 1.;
    vec3 col = vec3(0.0)+checkerTexture(uv*0.5, 0.02);
    data.m.rough = 0.5;
    data.m.metallic = 0.55;
    return col;
}

vec3 woodTexture(in vec2 uv, in vec3 p, inout Material m) {
    uv += 0.5928312;
    float tile = 2.0;
    vec2 id = floor(uv*tile);
    vec2 lv = fract(uv*tile);
    vec2 alv = abs(lv*2.0-1.0);
    lv = lv*lv*(3.0-2.0*lv);
    float seed = 0.392812;
    float rid = mix(
        mix(hash21Fast(id, seed), hash21Fast(id+vec2(1, 0), seed), lv.x),
        mix(hash21Fast(id+vec2(0, 1), seed), hash21Fast(id+vec2(1, 1), seed), lv.x),
        lv.y
    );
    uv *= rot(rid*0.5);
    vec3 col = vec3(0.0);
    vec3 c1 = rgb(142, 107, 94);
    vec3 c2 = rgb(79, 53, 57);
    vec3 c3 = rgb(174, 109, 69);
    vec3 c4 = rgb(232, 186, 128);
    vec3 hf1 = sineNoise(uv, 0.3292123, 2.);
    uv *= rot(hf1.z*0.5);
    vec3 col1 = mix4(c1, c2, c3, c4, hf1);
    vec3 col2 = mix4(c4, c3, c2, c1, hf1);
    col += (col1+col2)*0.5;
    float cracks = max(0.0, 1.0-smoothstep(0.02, 0.1, abs(hf1.y-rid)));
    float cracks2 = exp((sin((uv.x*4.928931+uv.y)*4.28*2.+cos((uv.y*4.203921)-(uv.x*0.59291))))-1.);
    cracks = mix(cracks, cracks2, clamp(hf1.z+hf1.y, 0.0, 1.0));
    cracks = mix(cracks, fract(cracks*3.3), 0.7*smoothstep(0.4, 0.7, hf1.x));
    cracks *= 0.5;
    col = mix(col, col*col, cracks);
    m.rough = clamp(cracks*2., 0.7, 1.0);
    m.spec = max(0.0, 0.3-cracks*2.);
    m.spec += hf1.y*0.5;
    m.z = (0.5-cracks*0.02)+((0.5-hf1.y)*2.);
    return col;
}

vec3 getAlbedoBox(inout Data data) {
    vec3 p = data.p;
    vec3 n = data.n;
    vec2 uv = boxUv(p, n);
    vec3 col = woodTexture(uv, p, data.m);
    data.n = makeNormal(data.n, data.m.z, 0.16, 0.009);
    return col;
}

vec3 getAlbedo(inout Data data) {
    switch (data.id) {
        case ID_GROUND: return getAlbedoGround(data); break;
        case ID_BOX: return getAlbedoBox(data); break;
    }

    return vec3(0.77);
}

vec3 getSky(in vec3 rd) {
    float dotup = cdot(rd, vec3(0, 1, 0));
    vec3 col = pow(COLOR_SKY, vec3(1.0 + dotup));
    return col;
}


