float fogSDF(in vec3 p, inout vec3 n, in Light light, in vec3 ro, in vec3 rd) {
    n = snoise3D((p*3.)+4.0*vec3(cos(T), sin(T), sin(T)), 3.302921, 0.0, 0.5, 4);
    float line = lineSDF(p, (ro)-(p+rd), light.p*FAR, 2.);
    vec3 L = getLightDir(light, p);
    line -= smoothstep(20.0, 0.0, length(p));
    return smin(line+length(n), clamp(length(n)+0.2*dot(normalize(p), -normalize(n*2.0-1.0)), 0.0, 1.0), 0.5);
}

vec3 marchFog(in vec3 ro, in vec3 rd, in Light light, in Data data) {
    float d = 0.0;

    int steps = 32;
    vec3 n = vec3(0.0, 0, 1);
    vec3 col = vec3(0.0);
    float c = 0.0;

  //  float world = 0.0;
    for (int i = 0; i < steps; i++) {
        vec3 p = ro+rd*(d);
       // float nextWorld = sdf(tmp, p);
        //world += nextWorld;


        float distP = distance(p, data.p);
        vec3 dirp = normalize(data.p - p);


        float next = fogSDF(p, n, light, ro, rd);
        d += next;



        if (abs(d) > 0.0000002) {

            Data tmp2 = NEW_DATA;
            vec3 L = getLightDir(light, p);
            n = normalize(n-normalize(p)*0.33);
            march(tmp2, ro,rd);// reflect(rd, normalize(n-L)));


            float NdotL = max(0.25, dot(n, L));

            col += NdotL*max(0.0, 1.0-next)*smoothstep(0.035, 1.0, tmp2.d/FAR);
            c += 1.;

        }

        if (d >= FAR) break;
    }

  //  d < data.d

    col /= max(1.0, c*0.25);
    return clamp(col, 0.0, 1.0);
}

vec3 render(inout Data data, in vec3 ro, in vec3 rd, inout float depth) {
    vec3 col = vec3(0.0);
    depth = 1.0;

    Light lights[NUM_LIGHTS];
    lights[0] = LIGHT_SUN; 

    raycast(data, lights, col, ro, rd);

    depth = data.d / FAR;


    vec3 L = getLightDir(lights[0], ro);
    col += pow(cdot(rd, L), 32.)*lights[0].c;

    col += marchFog(ro, rd, lights[0], data);
    return col;
}

Ray getRay(in vec2 uv, in vec4 m) {
    vec3 ro = vec3(0, 0, -6.0);
    vec3 rd = normalize(vec3(uv.xy, 1.0));

    if (m.z > 0.01 && length(m.xy) > 0.0001 && iFrame > 1 && iTime > 0.0001) {
        ro.yz *= rot(m.y*TAU);
        ro.xz *= rot(m.x*TAU);

        rd.yz *= rot(m.y*TAU);
        rd.xz *= rot(m.x*TAU);
    } else {
        ro.z = 6.;
        ro.x = cos(T*0.7)*0.7;
        ro.y = 2.5;
        rd = look(uv, vec3(1, mix(2.0, 0.5, 0.5+sin(T)*0.5), -3), ro, 1.);
    }

    float y = 0.5;
    ro.y += y;
    ro.y = max(ro.y, y);

    return Ray(ro, rd);
}

