// Utilities

vec3 mix4(vec3 c1, vec3 c2, vec3 c3, vec3 c4, vec3 m) {
    return mix(mix(mix(c1, c2, m.x), c3, m.y), c4, m.z);
}

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
    return mix(a, b, h) - k*h*(1.0-h);
}

float smax(float a, float b, float k) {
    return smin(a, b, -k);
}

vec3 smin(vec3 a, vec3 b, float k) {
    vec3 h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
    return mix(a, b, h) - k*h*(1.0-h);
}

vec3 smax(vec3 a, vec3 b, float k) {
    return smin(a, b, -k);
}

float sabs(in float v, in float k) {
    return smax(-v, v, k);
}

float sclamp(in float v, in float mi, in float ma, in float k) {
    return smin(smax(v, mi, k), ma, k);
}

vec3 makeNormal(in vec3 wn, in float z, in float mx, in float s) {
    if (abs(z) <= 0.0000001) return wn;
    vec2 dx = vec2(dFdx(z), 0.0001);
    vec2 dy = vec2(0.0001, dFdy(z));
    float bumpScale = s;
    vec3 cp = cross(vec3(dx, bumpScale), vec3(dy, bumpScale));
    if (length(cp) <= 0.00002) return wn;
    cp = normalize(cp);
    mx *= max(0.0, 1.0 - max(0.0, dot(cp, -wn)));
    vec3 next = normalize(mix(wn, normalize(wn+cp), mx));
    if (length(next) <= 0.0001) return wn;
    return next;
}

mat2 rot(in float a) { float c = cos(a); float s = sin(a); return mat2(c, s, -s, c); }

vec3 look(in vec2 uv, in vec3 point, in vec3 ro, in float zoom) {
    vec3 camForward = normalize(point - ro);
    vec3 camRight = -normalize(cross(camForward, vec3(0, 1, 0)));
    vec3 camUp = -normalize(cross(camRight, camForward));
    vec3 screenCenter = ro + camForward * zoom;
    vec3 screenIntersection = screenCenter + uv.x * camRight + uv.y * camUp;
    return normalize(screenIntersection - ro);
}

vec3 pointRot(in vec3 p, in vec3 r) {
    p.yz *= rot(r.x);
    p.xz *= rot(r.y);
