float sphereSDF(in vec3 p, in float r) {
    return length(p) - r;
}

float boxSDF(in vec3 p, in vec3 s) {
    p = abs(p) - s;
    return length(max(p, 0.0)) + min(max(p.x, max(p.y, p.z)), 0.0);
}

float lineSDF(in vec3 p, in vec3 a, in vec3 b, in float r) {
    vec3 pa = p - a;
    vec3 ba = b - a;
    return length(pa - ba * (clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0))) - r;
}

float cylSDF(in vec3 p, in vec3 a, in vec3 b, in float r) {
    vec3 ab = b - a;
    vec3 ap = p - a;
    float t = dot(ab, ap) / dot(ab, ab);
    vec3 c = a + t * ab;
    float x = length(p - c) - r;
    float y = (abs(t - 0.5) - 0.5) * length(ab);
    float e = length(max(vec2(x, y), 0.0));
    float i = min(max(x, y), 0.0);
    return e + i;
}

float torusSDF(in vec3 p, in float r1, float r2) {
    p = p.yzx;
    float ll = length(p.yz)-r1;
    return length(vec2(ll,p.x))-r2;
}

// Noise functions

uint hash21u(in vec2 ip, in float seed) {
    uvec2 p = uvec2(floatBitsToUint(ip.x), floatBitsToUint(ip.y));
    uint s = floatBitsToUint(seed);
    s ^= ~s >> 3U;
    p ^= (p << 17U);
    s ^= (~p.x);
    s ^= (~p.y);
    p ^= (p >> 11U);
    p ^= (p << 5U);
    p ^= (s << 3U);
    return ((p.x + p.y) ^ (p.x * s + p.y))*293U;
}

float hash21(in vec2 ip, in float seed) { return float(hash21u(ip, seed)) / float(0xFFFFFFFFU); }

vec3 hash23(in vec2 ip, in float seed) {
    uint n = hash21u(ip, seed);
    n ^= (n >> 13U);
    return vec3(float((n >> 16U) & 0xFFU), float((n >> 8U) & 0xFFU), float(n & 0xFFU)) / float(0xFFU);
}

float hash21Fast(in vec2 p, in float seed) {
    float ma = 64.092391;
    float x = mod(p.x*4.0291, ma) / ma;
    float y = mod(p.y*5.5920, ma) / ma;
    vec2 p2 = mix(vec2(x,y), vec2(y, x), fract(seed*10.29891231))*2.0-1.0;
    float r1 = mod(100.2891382*fract(seed+(dot(p, p2)*16.2099123)), ma)/ma;
    float r2 = fract((p.x*3.392912+p.y)*distance(p2, p-seed));
    return fract(dot(p, vec2(r1, r2)));
}

vec3 sineMut(in vec2 p, in float seed) {
    vec2 s1 = vec2(sin(p.x+seed), cos(p.y+seed));
    float r1 = dot(s1, p);
    s1 *= rot(r1);
    vec2 s2 = vec2(cos(r1+seed), sin(r1+seed));
    float r2 = dot(s2, p);
    s2 *= rot(r2);

    vec3 v = vec3(s1.x, s1.y, s2.x);
    v.xz *= rot(s2.y);
    return exp(v-1.);
}

vec3 sineNoise(in vec2 p, in float seed, in float freq) {
    float div = 0.0;
    float amp = 1.0;
    vec3 n = vec3(0.0);

    for (int i = 0; i < 4; i++) {
        n += amp*sineMut(p*freq, seed); div += amp; amp /= 2.; freq *= 2.0;
        p += (n.xz*n.y)*amp;
    }

    return n/div;
}

float noise(in vec2 p, in float seed) {
    vec2 id = floor(p);
    vec2 lv = fract(p);
    lv = lv*lv*(3.0-2.0*lv);
    return mix(
        mix(hash21(id, seed), hash21(id+vec2(1,0), seed), lv.x),
        mix(hash21(id+vec2(0, 1), seed), hash21(id+vec2(1,1), seed), lv.x),
        lv.y
    );
}

float noiseFast(in vec2 p, in float seed) {
    p += 0.0982812+seed;
    vec2 id = floor(p);
    vec2 lv = fract(p);
    lv = lv*lv*(3.0-2.0*lv);
    return mix(
        mix(hash21Fast(id, seed), hash21Fast(id+vec2(1,0), seed), lv.x),
        mix(hash21Fast(id+vec2(0, 1), seed), hash21Fast(id+vec2(1,1), seed), lv.x),
        lv.y
    );
}

float noiseFast(in vec2 p, in float seed, in float freq, const in int octaves) {
    p += 0.0982812+seed;
    float div = 0.0;
    float amp = 1.0;
    float n = 0.0;
    for (int i = ZERO; i < octaves; i++) {
        n += amp * noiseFast(p*freq, seed); div += amp; amp /= 2.0; freq *= 2.0;
    }
    return n / div;
}

#define NOISE(p, seed, lod) (textureLod(iChannel3, (p + (seed*1.9287827185))/256.0, lod).rgb)

vec3 snoise(in vec2 p, in float seed, in float lod) {
    p += 0.99288124;
    vec2 id = floor(p);
    vec2 lv = fract(p); lv = lv*lv*(3.0-2.0*lv);
    return mix(
        mix(NOISE(id, seed, lod), NOISE(id + vec2(1, 0), seed, lod), lv.x),
        mix(NOISE(id + vec2(0, 1), seed, lod), NOISE(id + vec2(1, 1), seed, lod), lv.x),
        lv.y
    );
}

vec3 snoise(in vec2 p, in float seed, in float lod, in float freq, const in int octaves) {
    p += 0.99288124;
    float div = 0.0;
    float amp = 1.0;
    vec3 n = vec3(0.0);
    for (int i = ZERO; i < octaves; i++) {
        n += amp * snoise(p*freq, seed, lod); div += amp; amp /= 2.0; lod /= 2.0; freq *= 2.0;
    }
    return n / div;
}

vec3 snoiseWarp(in vec2 p, in float seed, in float lod, in float freq, const in int octaves, in float warp) {
    p += 0.99288124;
    float div = 0.0;
    float amp = 1.0;
    vec3 n = vec3(0.0);
    for (int i = ZERO; i < octaves; i++) {
        n += amp * snoise(p*freq, seed, lod); div += amp; amp /= 2.0; lod /= 2.0; freq *= 2.0;
        p += (n.xy*n.z)*warp;
    }
    return n / div;
}

float voronoi(in vec2 p, in float seed, out vec2 id){
    vec2 Pi = floor(p);
    vec2 Pf = fract(p);

    float dmin = 1e20;
    vec2 res = vec2(0.0);
    float r = 1.0;

    for(int j=-1; j<=1; j++){
        for(int i=-1; i<=1; i++){
            vec2 lattice = vec2(float(i),float(j));
            vec2 offset = vec2(hash21(Pi+lattice, 0.9231234), hash21(Pi+lattice, 78.38381));
            vec2 diff = lattice + offset - Pf;

            float dist = dot(diff,diff);
            r = min(r, dist/0.56);
            if(r<dmin){
                id = offset;
                dmin = r;
                res = lattice + offset;
            }
        }
    }

    return clamp(sqrt(dmin), 0.0, 1.0);
}

// Shading & Colors

float luma(vec3 color) { return dot(color, vec3(0.299, 0.587, 0.114)); }

vec3 aces(vec3 x) {return x*(2.51*x + .03) / (x*(2.43*x + .59) + .14); }

vec3 fresnelSchlickRoughness(float NdotV, vec3 F0, float rough) {
  return F0 + (max(vec3(1.0 - rough), F0) - F0) * pow(max(1.0 - NdotV, 0.0), 5.0);
}

vec3 fresnelSchlick(vec3 F0, float cosTheta) {
	return F0 + (vec3(1.0) - F0) * pow(1.0 - cosTheta, 5.0);
}

float ndfGGX(float cosLh, float roughness) {
	float alpha   = roughness * roughness;
	float alphaSq = alpha * alpha;

	float denom = (cosLh * cosLh) * (alphaSq - 1.0) + 1.0;
	return alphaSq / (M_PI * denom * denom);
}

float gaSchlickG1(float cosTheta, float k) {
	return cosTheta / (cosTheta * (1.0 - k) + k);
}

float gaSchlickGGX(float cosLi, float cosLo, float roughness) {
	float r = roughness + 1.0;
	float k = (r * r) / 8.0;
	return gaSchlickG1(cosLi, k) * gaSchlickG1(cosLo, k);
}

vec3 EnvBRDFApprox(vec3 specularColor, float roughness, float ndotv) {
    const vec4 c0 = vec4(-1, -0.0275, -0.572, 0.022);
    const vec4 c1 = vec4(1, 0.0425, 1.04, -0.04);
    vec4 r = roughness * c0 + c1;
    float a004 = min(r.x * r.x, exp2(-9.28 * ndotv)) * r.x + r.y;
    vec2 AB = vec2(-1.04, 1.04) * a004 + r.zw;
    return specularColor * AB.x + AB.y;
}

// Marching

bool march(inout Data data, in vec3 ro, in vec3 rd) {
    float d = 0.0;
    for (int i = ZERO; i < STEPS; i++) {
        vec3 p = ro+rd*d;
        float next = sdf(data, p);
        d += next;
        if (abs(next) <= (NEAR * (1.0 + (d / FAR_LOD)))) break;
        if (abs(d) >= FAR) return false;
    }

    vec3 p = ro+rd*d;
    vec2 e = vec2(NORMAL_RANGE, 0.0);
    vec3 n = normalize(sdf(data, p) - vec3(
        sdf(data, p - e.xyy),
        sdf(data, p - e.yxy),
        sdf(data, p - e.yyx)
    ));

    data.d = d;
    data.p = p;
    data.n = n;

    return true;
}

// Modified version of iq's https://www.shadertoy.com/view/lsKcDD
float getShadow(in vec3 ro, in vec3 rd, in vec3 n, in float near, in float far, in int skip) {
    Data data = NEW_DATA;
    data.skip = skip;
    float NdotL = dot(n, rd);
    float res = 1.0;
    float ep = 1e20;
    float t = NEAR;
    float ss = (far/FAR)/2.;
    for(int i = 0; i < SHADOW_STEPS && t < far; i++){
        vec3 p = ro + rd*t;        
        float h = sdf(data, p);
        float y = h*h / (2.9 * ep);
        float d = sqrt(h*h-y*y);
        res = min(res, d/(ss*max(0.0, t-y)));
        ep = h;
        t += h;
    }

    // Fixes issues with sharp corners & edges
    float ra = far*0.5; // radius
    float fe = ra*0.77; // feather
    res = max(res, 0.);
    res = max(mix(res*res,
        (1.0-smoothstep(ra-fe, ra+fe, far-t)),
        smoothstep(0.0, 0.6, res+smoothstep(0.0, 0.9, t / (1.0+t*6.)))
    ), 0.);
    //////////////////////////////////////////

    return clamp(res, AMBIENT, 1.);
}


// Lighting

struct Light { vec3 p; vec3 d; vec3 c; float s; int type; };
#define LIGHT_AMBIENT 0
#define LIGHT_POINT 1
#define LIGHT_DIR 2

vec3 getLightDir(in Light light, in vec3 p) {
    return light.type == LIGHT_AMBIENT ? normalize(light.p) :
           light.type == LIGHT_POINT ? normalize(light.p - p) :
           normalize(light.d);
}

vec3 getLightAtt(in Light light, in vec3 p, in vec3 n) {
    vec3 L = getLightDir(light, p);
    float NdotL = max(AMBIENT, dot(n, L));

    switch (light.type) {
        case LIGHT_AMBIENT: return light.c * light.s * NdotL; break;
        case LIGHT_POINT: return light.c * NdotL * (
            pow(light.s, 2.0) / max(0.0001, pow(distance(light.p, p), 2.0))
        ); break;
        case LIGHT_DIR: return light.c * light.s * NdotL * smoothstep(0.8, 1.0, dot(
            normalize(light.p - p),
            L
        )); break;
    }

    return vec3(0.0);
}

vec3 forEachLight(in Light light, in Data data, in vec3 ro, in vec3 rd, in vec3 diffuse) {
    vec3 p = data.p;
    vec3 n = data.n;
    vec3 L = getLightDir(light, p);
    vec3 ref = reflect(L, n);
    float VdotR = cdot(rd, ref);
    float spec = data.m.spec * pow(VdotR, SPECULAR_AMPLITUDE);
    vec3 att = getLightAtt(light, p, n);
    float shadow = getShadow(p + (n*NEAR*2.0), L, n, data.d, light.type == LIGHT_AMBIENT ? FAR :
        distance(light.p, p), data.id);

    float metallic = data.m.metallic;
    float rough = data.m.rough;
    float specF = data.m.spec;

    vec3 LH = normalize(L + -rd);
    float cosLi = max(0.0001, dot(n, L));
	float cosLh = max(0.0001, dot(n, LH));
    float cosLo = max(0.0001, dot(n, -rd));
    vec3 F0 = mix(vec3(0.04), diffuse, metallic);
    vec3 F  = fresnelSchlick(F0, max(0.0, dot(LH, -rd)));
    float D = ndfGGX(cosLh, rough);
    float G = gaSchlickGGX(cosLi, cosLo, rough);
    vec3 kd = mix(vec3(1.0) - F, vec3(0.0), metallic);
    vec3 diffuseBRDF = kd * diffuse;
    vec3 specularBRDF = (F * D * G) / max(0.00003, 4.0 * cosLi * cosLo);


    return (diffuseBRDF + specF*specularBRDF) * att * shadow; 
}



bool rayGetColor(inout Data data, in Light lights[NUM_LIGHTS],
    in bool with_sky, inout vec3 col, in vec3 ro, in vec3 rd, inout vec3 diffuse) {
    if (!march(data, ro, rd)) {
        if (with_sky) {
            col += getSky(rd);
        }
        return false;
    }
    vec3 albedo = getAlbedo(data);
    diffuse = albedo / M_PI;
    for (int i = ZERO; i < NUM_LIGHTS; i++) {
        Light light = lights[i];
        col += forEachLight(light, data, ro, rd, diffuse);
    }
    return true;
}

bool raycast(
    inout Data data,
    in Light lights[NUM_LIGHTS],
    inout vec3 col,
    in vec3 ro,
    in vec3 rd
 ) {

    vec3 diffuse = vec3(0.0);
    if (!rayGetColor(data, lights, true, col, ro, rd, diffuse)) return false;
    float rough = data.m.rough;
    float metallic = data.m.metallic;
    vec3 n = data.n;
    vec3 p = data.p;

    if (rough < 0.993) {
        vec3 F0 = mix(vec3(0.04), diffuse, metallic);
        float NdotV = max(0.0, dot(n, -rd));
        vec3 F = fresnelSchlickRoughness(NdotV, F0, rough);
        Data reflectData = NEW_DATA;
        vec3 reflectRo = p + (n*NEAR*3.);
        vec3 reflectDir = reflect(rd, n);
        vec3 reflectDiffuse = vec3(0.0);
        vec3 reflectColor = vec3(0.0);
        vec3 env = EnvBRDFApprox(vec3(0.77), rough*rough, NdotV);
        reflectData.skip = data.id;

        rayGetColor(reflectData, lights, true, reflectColor, reflectRo, reflectDir, reflectDiffuse);

        col += reflectColor * (F * env.x * env.y);
    }

    return true;
}
