void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates
    vec2 uv = (2.0*fragCoord - iResolution.xy) / iResolution.yy;
    vec2 p = uv*2.0; // scale so the shape doesn't fill the entire screen

    // Modulation factor (exponent) of the distance from center
    float m = pow(4.0, 0.25+0.99*sin(iTime));

    // Modulate the distance
    p = pow(abs(p), vec2(m));

    // Gaussian
    float g = exp(-dot(p, p));

    // Output to screen
    fragColor = vec4(g, g, g, 1.0);
}
