// Green screen function from @iq 

// :D :D :D
vec4 compositeGreenScreenTexture(vec2 uv, vec4 background)
{

     vec4 tex = texture(iChannel0, uv);

     float maxrb = max( tex.r, tex.b );
     float k = clamp( (tex.g-maxrb)*5.0, 0.0, 1.0 );

     float ll = length( tex );
     tex.g = min( tex.g, maxrb*0.8 );
     tex = ll*normalize(tex);

     return mix(tex, background, k);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    const float PI = 3.1415926f;
    const float speed = 0.1f;

    float move = speed * iDate.w;

    float s = uv.x + uv.y;
    // Time varying pixel color
    float r = clamp(sin(fract(s * 3.0f) * 2.0f * PI), 0.0f, 1.0f); // fixed
    float g = clamp(sin(fract(s * 3.0f + 0.33333f + move) * 2.0f * PI), 0.0f, 1.0f); // move up
    float b = clamp(sin(fract(s * 3.0f + 0.66667f - move) * 2.0f * PI), 0.0f, 1.0f); // move down
    vec4 col = vec4(r, g, b, 1.0);

    // Output to screen
    fragColor = col;
}
