vec3 hsv(float h, float s, float v){
    vec4 t = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + t.xyz) * 6.0 - vec3(t.w));
    return v * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    float i,e,R,s, t = iTime;
    vec4 o;
    vec3 q,p,d=vec3(fragCoord.yx/iResolution.x+vec2(-.3,.3),1);

    d /= pow(sqrt(dot(d,d)-1.500),.5);

    for(q.zy--;i++<30.;){
        o.rgb += hsv(.15 + sin(iTime*.5)*.05,-e*99.,fwidth(e/1.));
        s=9.;
        p = q += d*e*R*(.15+sin(iTime)*.1);
        p = vec3(log(R=length(p)), exp2(p.x+R-p.z/R), atan(p.y-d.y,p.x));

        for(e=--p.y;s<10.;s+=s)
            e += dot(cos(p.xz*s-t),cos(p.xz*s*R-t))/s;
     }

    fragColor = o;
}
