float distLine(in vec2 uv, in vec2 x1, in vec2 x2)
{
    vec2 a = uv - x1;
    vec2 b = x2 - x1;
    float t = clamp(dot(a,b)/dot(b,b), 0.0, 1.0);
    //float t = dot(a,b)/dot(b,b);
    vec2 r = a - t*b;
    return length(r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / (0.5 * min(iResolution.x,iResolution.y));

    //uv = uv * 5.0;

    vec2 x1 = vec2(0.35, 0.36);
    vec2 x2 = vec2(-0.2, 0.5);
    float d = distLine(uv, x1, x2);
    float r = smoothstep(0.05, 0.06, d);

    fragColor = vec4(vec3(r), 1.0);
}
