mat2 rotate2D(float rad){
    return mat2(cos(rad), sin(rad), -sin(rad), cos(rad));
}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e)*4.;
    p=vec3(p.x*p.x+p.y*p.y, p.x*p.y,tan(p.z+iTime));
    length(p.xy)<3.?p.xy*=rotate2D(iTime*.5):p.xy;

    for(j=0.;j++<3.;)
        p=abs(abs(p)-.06)-1.6,
        p=cross(ceil(p),p),
        d-=dot(p,p)/2e4;

    e+=d;
}o+=vec4(p,1)/i;
}
