void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // line direction
    vec3 p = vec3(0.6,0.8,.1);
    // distance to line
    float d = dot(p,vec3(uv,1.));

    vec3 color = texture(iChannel0,uv).xyz;
    float intensity = dot(color,vec3(1./3.f));

    //map [0-1] to discrete steps 0/N,1/N,2/N,3/N ...
    const int nofSteps = 8;
    float s = floor(intensity * float(nofSteps)) / float(nofSteps);


    fragColor = vec4(mod(400.*(1.-s)*d,1.)<0.20+0.79*s);
    if(fragCoord.x < iMouse.x)
      fragColor = vec4(color,1);

}
