void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float speed = 0.01;
    float size = 300.0;
    float thickness = 0.12;
    float frequency = 12.0;

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv *= frequency;

    float pattern = mod(floor(uv.x) + floor(uv.y), 4.0);
    float xDisplace = sin(uv.x + iTime * speed) * thickness;
    float yDisplace = cos(uv.y + iTime * speed) * thickness;

    vec3 blue = vec3(0.0, 0.0, 1.0);
    vec3 orange = vec3(1.0, 0.5, 0.0);

    vec3 color = mix(blue, orange, pattern);
    color = mix(color, vec3(1.0), length(fract(uv * size) - 0.5));
    color = mix(color, vec3(0.0), length(fract((uv + vec2(xDisplace, yDisplace)) * size) - 0.5));

    fragColor = vec4(color, 1.0);
}
