void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    uv.y *= iResolution.y / iResolution.x;
    float circleRadius = 0.2; // Circle size controlled by time
    float distanceFromCenter = length(uv);

    // Create a circle mask
    float circle = smoothstep(circleRadius - 0.01, circleRadius, distanceFromCenter);

    // Convert the inside of the circle to black and white
    vec3 color = texture(iChannel0, fragCoord.xy / iResolution.xy).rgb;
    float bw = dot(color, vec3(0.299, 0.587, 0.114));
    color = mix(vec3(bw), color, circle);

    // Output the color
    fragColor = vec4(color, 1.0);
}
