#ifdef GL_ES
precision mediump float;
#endif


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
    st = st*2.-1.;
    //st -= 1.;
    vec3 color = vec3(0.0);
    vec2 pos = vec2(.8,0.) - st;
    float r = length(pos)*2.0;
    float r1 = fract(r*3.0+iTime);
    vec3 color2 = vec3(.1,.9,r1);
    color2 = 1. - color2;
    float a = atan(pos.y,pos.x);
    float f = cos(a*+iTime);

    f = abs(cos(a*12.+iTime*4.)*sin(a*3.+iTime*4.))*.8+.1;
    color = vec3(smoothstep(f,f+.5,r));
    color += vec3(pow(f, 2.),.3,.7);
    color = 1. -color;
    color += color2;
    fragColor = vec4(color,1.);
}
