void mainImage( out vec4 o, in vec2 FC )
{
float i,d=.1,e;vec3 p;
o=vec4(0.0);

for(float i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    p=vec3(p.x*p.x+p.y*p.y,2.*p.x*p.y,cos(p.z-iTime*3.));

    for(float j=0.;j++<6.;){   
        p=abs(abs(p)-.04)-.008;
        d=pow(length(p),3.4);
        d=max(max(p.x,p.z),p.y);
        d-=dot(p,p)/2e4;
    }
    e+=d;

}o+=vec4(p*2.,1);
}
