void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float blurAmount = 10.0;
    vec4 color = vec4(0.0, 0.0, 0.0, 1.0); 
    vec2 currentCoordinate = vec2(0.0, 0.0);

    for(float i = blurAmount * -1.0; i <= blurAmount; i++)
    {
        currentCoordinate = vec2(fragCoord.x, fragCoord.y + i);
        color.r += texture(iChannel0, currentCoordinate / iResolution.xy).r;
        color.g += texture(iChannel0, currentCoordinate / iResolution.xy).g;
        color.b += texture(iChannel0, currentCoordinate / iResolution.xy).b;
    }

    color = vec4(color.r / (blurAmount*2.0+1.0), color.g / (blurAmount*2.0+1.0), color.b / (blurAmount*2.0+1.0), 1.0);
    fragColor = color;
}

