void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 3.*sin(.1*iTime)*(fragCoord/iResolution.xy-0.5);

    // Time varying pixel color
    vec3 col = vec3(sin(iTime*uv.yxy)*sin(10.*uv.xyx)*cos(20.*uv.yxy)*cos(10.*cos(10.*uv.xyx)));
    // Output to screen
    fragColor = vec4(col,1.0);
}
