void mainImage(out vec4 O, vec2 I)
{    
    for(vec4 t=iTime+vec4(0,11,33,0),p=O=t/t;
        O.w>0.&& ((~int(p.zx*=mat2(cos(t-sin(t*8.)/8.1))) ^ int(p) ^ int(p.y))&127)>0;
        p = vec4(I+I-iResolution.xy, O*1e3)*.1)
    O-=5e-3;
}

