// iq HSV to RGB conversion https://www.shadertoy.com/view/MsS3Wc
vec3 hsv(in vec3 c)
{
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  return c.z * mix( vec3(1.0), rgb, c.y);
}

float sdBestagon(vec2 p, float r) {
  p = abs(p);
  float d = 1e100;
  d = dot(p, normalize(vec2(1., sqrt(3.))));
  d = max(d, p.x);
  return d - r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float sf = 1. / min(iResolution.x, iResolution.y);
  vec2 uv = sf * (fragCoord - .5 * iResolution.xy);
  vec3 color = vec3(0);
  float t = iTime*13.;

  for (float i = 0.; i < 1.; i+=1./40.) {
    float t2 = t * ((1. - i) * .62832);
    vec2 p = uv - vec2(sin(t2), cos(t2))*.01;
    float d = sdBestagon(p, 1.0 * i);
    d = abs(d) - 0.1 * ((i - 0.4 + 0.4)*.2);
    d = smoothstep(sf, -sf, d);
    color = mix(color, hsv(vec3(sin(t2/4.) * .1 + .05,1,1)), d) * (i*0.02 + 0.98);
  }

  fragColor = vec4(color, 1);
}
